import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = process.env.REACT_APP_API_URL || "https://uk-portal-3.onrender.com";
  const token = localStorage.getItem("familyToken");

  // Get last 6 months including current
  const getLastSixMonths = () => {
    const months = [];
    const date = new Date();
    for (let i = 0; i < 6; i++) {
      const d = new Date(date.getFullYear(), date.getMonth() - i, 1);
      months.push(d.toISOString().slice(0, 7)); // YYYY-MM
    }
    return months.reverse();
  };

  const currentMonth = new Date().toISOString().slice(0, 7);

  // Fetch family data
  const fetchFamily = async () => {
    if (!token) {
      setMsg("⚠️ No authentication token found. Please login.");
      return;
    }
    try {
      setLoading(true);
      const res = await axios.get(`${API_BASE}/api/family/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setMsg("");
    } catch (err) {
      console.error("Error fetching family profile:", err.response || err);
      setMsg(
        err.response?.data?.message ||
        "⚠️ Failed to load family data. Please login again."
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFamily();
  }, [token, API_BASE]);

  // Upload document
  const handleUpload = async () => {
    if (!file) return setMsg("⚠️ Please select a file");
    try {
      setLoading(true);
      const formData = new FormData();
      formData.append("document", file);

      const res = await axios.post(`${API_BASE}/api/family/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      if (res.data.family) setFamily(res.data.family);
      setFile(null);
      setMsg("✅ File uploaded successfully");
    } catch (err) {
      console.error("Upload error:", err.response || err);
      setMsg(err.response?.data?.message || "⚠️ Upload failed");
    } finally {
      setLoading(false);
    }
  };

  // Delete document
  const handleDelete = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;
    try {
      setLoading(true);
      const res = await axios.delete(`${API_BASE}/api/family/document/${docId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.family) setFamily(res.data.family);
      setMsg("✅ Document deleted successfully");
    } catch (err) {
      console.error("Delete error:", err.response || err);
      setMsg(err.response?.data?.message || "⚠️ Failed to delete document");
    } finally {
      setLoading(false);
    }
  };

  // Admin marks current month as paid
  const handleMarkPaid = async (month) => {
    if (!token || !family) return;

    try {
      setLoading(true);
      const taxEntry = family.taxHistory?.find((t) => t.month === month);
      const amount = taxEntry ? taxEntry.amount : 0;

      await axios.post(
        `${API_BASE}/api/admin/families/tax`,
        { familyId: family._id, month, amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Update local state
      setFamily((prev) => {
        const updatedHistory = [...(prev.taxHistory || [])];
        const index = updatedHistory.findIndex((t) => t.month === month);
        if (index !== -1) updatedHistory[index].paid = true;
        else updatedHistory.push({ month, amount, paid: true });
        return { ...prev, taxHistory: updatedHistory };
      });

      setMsg("✅ Tax marked as paid");
    } catch (err) {
      console.error(err.response || err);
      setMsg("⚠️ Failed to update tax");
    } finally {
      setLoading(false);
    }
  };

  if (!family)
    return (
      <div className="p-8 text-center text-lg font-semibold animate-pulse">
        ⏳ Loading family data...
      </div>
    );

  const lastSixMonths = getLastSixMonths();

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center mb-6 text-green-700">
        🏠 Family Dashboard
      </h2>

      {loading && <p className="text-center text-blue-600 font-semibold">Processing...</p>}
      {msg && <p className="text-center text-green-700 font-medium">{msg}</p>}

      {/* Family Info */}
      <table className="w-full border border-gray-200 rounded-lg overflow-hidden">
        <tbody>
          <tr className="bg-gray-50">
            <td className="p-2 font-semibold">Family ID</td>
            <td className="p-2">{family.familyId}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Leader</td>
            <td className="p-2">{family.leaderName}</td>
          </tr>
          <tr className="bg-gray-50">
            <td className="p-2 font-semibold">Members</td>
            <td className="p-2">{family.members?.join(", ") || "-"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Address</td>
            <td className="p-2">{family.address || "-"}</td>
          </tr>
          <tr className="bg-gray-50">
            <td className="p-2 font-semibold">Email</td>
            <td className="p-2">{family.email || "-"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Phone</td>
            <td className="p-2">{family.phone || "-"}</td>
          </tr>
        </tbody>
      </table>

      {/* Tax Info */}
      <div className="border p-4 rounded">
        <h3 className="font-semibold mb-2 text-lg">Tax Information</h3>
        <table className="w-full border border-gray-200 rounded text-center">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2 border">Month</th>
              <th className="p-2 border">Amount (INR)</th>
              <th className="p-2 border">Status</th>
              <th className="p-2 border">Action</th>
            </tr>
          </thead>
          <tbody>
            {lastSixMonths.map((month) => {
              const tax = family.taxHistory?.find((t) => t.month === month);
              const amount = tax ? tax.amount : month === currentMonth ? 0 : "-";
              const paid = tax?.paid || false;

              return (
                <tr key={month}>
                  <td className="p-2 border">{month}</td>
                  <td className="p-2 border">{amount}</td>
                  <td className={`p-2 border ${paid ? "text-green-600" : "text-red-600"}`}>
                    {paid ? "Paid" : "Pending"}
                  </td>
                  <td className="p-2 border">
                    {month === currentMonth && !paid ? (
                      <button
                        onClick={() => handleMarkPaid(month)}
                        className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition"
                      >
                        Mark Paid
                      </button>
                    ) : (
                      "-"
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Document Upload */}
      <div className="border p-4 rounded">
        <h3 className="font-semibold mb-2 text-lg">Upload Document</h3>
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          className="border border-gray-300 rounded px-2 py-1"
        />
        <button
          onClick={handleUpload}
          className="ml-2 bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition"
        >
          Upload
        </button>
      </div>

      {/* Uploaded Documents */}
      <div className="border p-4 rounded">
        <h3 className="font-semibold mb-2 text-lg">Uploaded Documents</h3>
        <ul className="list-disc pl-6">
          {family.documents?.length > 0 ? (
            family.documents.map((doc) => (
              <li key={doc._id} className="flex items-center justify-between mb-1">
                <div>
                  <span className="font-medium">{doc.filename}</span>{" "}
                  <span className="text-gray-500 text-sm">
                    ({new Date(doc.uploadedAt).toLocaleDateString()})
                  </span>
                </div>
                <div className="space-x-2">
                  <a
                    href={`${API_BASE}${doc.path}`}
                    target="_blank"
                    rel="noreferrer"
                    className="text-blue-600 underline"
                    download
                  >
                    Download
                  </a>
                  <button
                    onClick={() => handleDelete(doc._id)}
                    className="text-red-600 underline"
                  >
                    Clear
                  </button>
                </div>
              </li>
            ))
          ) : (
            <li>No documents uploaded.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default FamilyDashboard;
