import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const token = localStorage.getItem("adminToken");

  const fetchFamilies = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/families", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamilies(res.data);
    } catch (err) {
      console.error("Error fetching families:", err.response?.data || err.message);
    }
  };

  useEffect(() => {
    if (token) fetchFamilies();
  }, [token]);

  const handleApprove = async (familyId, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamilies((prev) =>
        prev.map((f) => (f._id === familyId ? { ...f, approved } : f))
      );
    } catch (err) {
      console.error("Error updating approval:", err.response?.data || err.message);
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-8">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Families</h2>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-green-700 text-white">
              <th className="border p-2">Family ID</th>
              <th className="border p-2">Leader Name</th>
              <th className="border p-2">Address</th>
              <th className="border p-2">Approved</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.map((f) => (
              <tr key={f._id}>
                <td className="border p-2">{f.familyId}</td>
                <td className="border p-2">{f.leaderName}</td>
                <td className="border p-2">{f.address}</td>
                <td className="border p-2">{f.approved ? "Yes" : "No"}</td>
                <td className="border p-2 space-x-2">
                  {!f.approved && (
                    <button
                      onClick={() => handleApprove(f._id, true)}
                      className="bg-green-600 text-white px-2 py-1 rounded"
                    >
                      Approve
                    </button>
                  )}
                  {f.approved && (
                    <button
                      onClick={() => handleApprove(f._id, false)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Reject
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
