import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AdminLogin = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Normalize API base URL
  const API_BASE =
    (process.env.REACT_APP_API_URL || "https://uk-portal-3.onrender.com").replace(
      /\/+$/,
      ""
    );

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setErr("⚠️ Username and Password are required.");
      return;
    }

    setLoading(true);
    setErr("");

    try {
      const res = await axios.post(`${API_BASE}/api/admin/login`, {
        username,
        password,
      });

      if (res.data?.token) {
        // Store token securely
        localStorage.setItem("adminToken", res.data.token);
        localStorage.setItem("adminUsername", username);
        navigate("/admin-dashboard");
      } else {
        setErr("⚠️ Unexpected response from server.");
      }
    } catch (error) {
      console.error("Login error:", error.response?.data || error.message);
      setErr(
        error.response?.data?.message ||
          "⚠️ Login failed. Please check your credentials."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <form
        onSubmit={handleLogin}
        className="bg-white p-6 rounded-lg shadow-md w-full max-w-md"
      >
        <h2 className="text-2xl font-bold mb-4 text-center text-green-700">
          Admin Login
        </h2>

        {err && <p className="text-red-500 mb-3 text-center">{err}</p>}

        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full border p-2 mb-3 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full border p-2 mb-4 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
          required
        />

        <button
          type="submit"
          disabled={loading}
          className={`w-full px-4 py-2 rounded text-white ${
            loading
              ? "bg-green-400 cursor-not-allowed"
              : "bg-green-700 hover:bg-green-800"
          } transition`}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
};

export default AdminLogin;
