// client/src/components/GoogleMap.jsx
import React, { useEffect, useRef } from "react";

const GoogleMap = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    // Load the Google Maps script dynamically
    const existingScript = document.getElementById("googleMaps");

    if (!existingScript) {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=AQ.Ab8RN6IvE7ZcBnL1WbZoxodlXDXIAHpOuQiXVvz7vZLBOTkOMw&callback=initMap`;
      script.id = "googleMaps";
      script.async = true;
      script.defer = true;
      document.body.appendChild(script);

      script.onload = () => {
        initMap();
      };
    } else {
      initMap();
    }

    function initMap() {
      if (mapRef.current) {
        const udaikulam = { lat: 9.9215, lng: 78.1197 }; // Udaikulam coordinates
        const map = new window.google.maps.Map(mapRef.current, {
          center: udaikulam,
          zoom: 15,
        });
        new window.google.maps.Marker({
          position: udaikulam,
          map,
          title: "Udaikulam Village",
        });
      }
    }
  }, []);

  return <div ref={mapRef} className="w-full h-96 rounded shadow-lg"></div>;
};

export default GoogleMap;
