import React, { useEffect, useState } from "react";
import axios from "axios";
import GalleryModal from "../components/GalleryModal";

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGallery = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/gallery");
        console.log("Gallery images:", res.data);

        // Replace spaces in URLs with %20
        const safeImages = res.data.map(img => ({
          ...img,
          url: img.url.replace(/ /g, "%20")
        }));

        setImages(safeImages);
      } catch (err) {
        console.error("Error fetching gallery:", err);
        setError("‚ö†Ô∏è Failed to load gallery. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchGallery();
  }, []);

  if (loading) return <div className="p-8 text-center text-lg font-semibold animate-pulse">‚è≥ Loading gallery...</div>;
  if (error) return <div className="p-8 text-center text-red-600 font-semibold">{error}</div>;

  return (
    <div className="p-8 max-w-7xl mx-auto">
      <h2 className="text-4xl font-bold mb-8 text-center text-green-700">
        üå∏ Village Photo Gallery
      </h2>

      {images.length === 0 ? (
        <p className="text-gray-600 text-lg text-center">No images available in the gallery.</p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {images.map((img, idx) => {
            const imageUrl = `http://localhost:5000${img.url}`;
            return (
              <div
                key={idx}
                className="relative group overflow-hidden rounded-xl shadow-md cursor-pointer"
                onClick={() => setSelectedImage(imageUrl)}
              >
                <img
                  src={imageUrl}
                  alt={img.title || `Gallery ${idx}`}
                  className="w-full h-48 object-cover transform group-hover:scale-110 transition duration-500 bg-gray-200"
                  onError={(e) => (e.target.src = "/fallback.png")}
                />
                <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition flex items-center justify-center text-white text-lg font-semibold">
                  {img.title || "View Image"}
                </div>
              </div>
            );
          })}
        </div>
      )}

      {selectedImage && <GalleryModal image={selectedImage} onClose={() => setSelectedImage(null)} />}
    </div>
  );
};

export default Gallery;
