import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");

  const token = localStorage.getItem("familyToken");

  useEffect(() => {
    const fetchFamily = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/family/details", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamily(res.data);
      } catch (err) {
        console.error("Error fetching family:", err);
        setError("Failed to load family data.");
      } finally {
        setLoading(false);
      }
    };

    fetchFamily();
  }, [token]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return setUploadMessage("Please select a file first.");
    setUploadMessage("");

    const formData = new FormData();
    formData.append("document", selectedFile);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/family/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setFamily(res.data);
      setUploadMessage("Document uploaded successfully!");
      setSelectedFile(null);
    } catch (err) {
      console.error("Upload error:", err);
      setUploadMessage("Failed to upload document.");
    }
  };

  if (loading) return <p className="p-6 text-gray-600">Loading family data...</p>;
  if (error) return <p className="p-6 text-red-500">{error}</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-2xl font-bold mb-4 text-center">üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Family Dashboard</h2>

      <div className="border rounded-lg p-4 shadow-md space-y-4">
        <p><strong>Family ID:</strong> {family.familyId}</p>
        <p><strong>Leader:</strong> {family.leaderName}</p>
        <p><strong>Members:</strong> {family.members.join(", ")}</p>
        <p><strong>Address:</strong> {family.address}</p>
        <p><strong>Phone:</strong> {family.phone}</p>
        <p><strong>Email:</strong> {family.email}</p>

        <div className="space-y-2">
          <h3 className="font-semibold">Documents</h3>
          {family.documents.length > 0 ? (
            <ul className="list-disc pl-5">
              {family.documents.map((doc, index) => (
                <li key={index}>
                  <a
                    href={`http://localhost:5000/${doc.filePath}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                  >
                    {doc.fileName}
                  </a>
                </li>
              ))}
            </ul>
          ) : (
            <p>No documents uploaded.</p>
          )}

          <input type="file" onChange={handleFileChange} className="border p-2 rounded" />
          <button
            onClick={handleUpload}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Upload Document
          </button>
          {uploadMessage && <p className="text-sm text-green-700">{uploadMessage}</p>}
        </div>
      </div>

      <div className="border rounded-lg p-4 shadow-md">
        <h3 className="font-semibold mb-2">Tax History</h3>
        {family.taxHistory.length > 0 ? (
          <ul className="list-disc pl-5">
            {family.taxHistory.map((tax, index) => (
              <li key={index}>
                {tax.month}: {tax.amount} - {tax.paid ? "Paid" : "Pending"}
              </li>
            ))}
          </ul>
        ) : (
          <p>No tax records found.</p>
        )}
      </div>
    </div>
  );
};

export default FamilyDashboard;
