// Placeholder for client/src/pages/Workers.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const Workers = () => {
  const [workers, setWorkers] = useState([]);

  useEffect(() => {
    axios.get("/api/village/workers")
      .then(res => setWorkers(res.data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">Worker Types</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {workers.map(worker => (
          <div key={worker._id} className="border rounded p-4 shadow hover:shadow-lg transition">
            <h3 className="text-xl font-semibold">{worker.type}</h3>
            <p className="text-gray-700">{worker.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Workers;
