import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FamilyLogin = () => {
  const [familyId, setFamilyId] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // clear previous errors

    try {
      const res = await fetch("http://localhost:5000/api/family", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ familyId, password }),
      });

      const data = await res.json();

      if (res.ok) {
        // Store JWT token and family ID for dashboard access
        localStorage.setItem("familyToken", data.token);
        localStorage.setItem("familyId", data.family._id);

        // Redirect to family dashboard
        navigate("/family-dashboard");
      } else {
        setError(data.message || "Login failed. Please try again.");
      }
    } catch (err) {
      console.error("Login error:", err);
      setError("Server error. Please try again later.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form
        onSubmit={handleLogin}
        className="bg-white p-8 rounded shadow-md w-full max-w-md"
      >
        <h2 className="text-2xl font-bold mb-6 text-center">Family Login</h2>

        {error && (
          <p className="text-red-600 mb-4 text-center">{error}</p>
        )}

        <input
          type="text"
          placeholder="Family ID"
          className="mb-4 w-full p-3 border rounded focus:outline-none focus:ring-2 focus:ring-green-500"
          value={familyId}
          onChange={(e) => setFamilyId(e.target.value)}
          required
        />

        <input
          type="password"
          placeholder="Password"
          className="mb-6 w-full p-3 border rounded focus:outline-none focus:ring-2 focus:ring-green-500"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button
          type="submit"
          className="w-full bg-green-700 hover:bg-green-800 text-white font-bold py-3 rounded transition duration-200"
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default FamilyLogin;
