// client/src/pages/Home.jsx
import React from "react";
import { useTranslation } from "react-i18next";
import GoogleMap from "./GoogleMap";
import "../styles.css"; // import styles
import Footer from "../components/Footer";

const Home = () => {
  const { t } = useTranslation();

  return (
    <div className="home-container">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">{t("welcome")}</h1>
          <p className="hero-subtitle">
            {t("explain")}
          </p>
          <button className="hero-button">üåø Explore More</button>
        </div>
      </section>

      {/* About Section */}
      <section className="about-section">
        <h2 className="section-title">üå∏ About Udaikulam</h2>
        <p className="section-text">
          Udaikulam is a vibrant village with rich traditions, a close-knit
          community, and a beautiful temple at its heart. Our mission is to
          preserve our culture while moving forward with digital empowerment.
        </p>
      </section>

      {/* Google Map Section */}
      <section className="map-section">
        <h2 className="section-title">üìç Find Us on Map</h2>
        <div className="map-container">
          <GoogleMap />
        </div>
      </section>
      <Footer/>
    </div>
  );
};

export default Home;
