import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Menu, X } from "lucide-react"; // install lucide-react for icons

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [lang, setLang] = useState("en");
  const [isOpen, setIsOpen] = useState(false);

  const toggleLanguage = () => {
    const newLang = lang === "en" ? "ta" : "en";
    i18n.changeLanguage(newLang);
    setLang(newLang);
  };

  return (
    <nav className="bg-green-700 text-white p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        {/* Logo / Title */}
        <Link to="/" className="text-lg font-bold">
          {t("home")}
        </Link>

        {/* Desktop Links */}
        <div className="hidden md:flex space-x-6 items-center">
          <Link to="/history" className="hover:text-yellow-300">{t("history")}</Link>
          <Link to="/events" className="hover:text-yellow-300">{t("events")}</Link>
          <Link to="/gallery" className="hover:text-yellow-300">{t("gallery")}</Link>
          <Link to="/workers" className="hover:text-yellow-300">{t("workers")}</Link>
          <Link to="/family-login" className="hover:text-yellow-300">{t("familyLogin")}</Link>
          <Link to="/admin-login" className="hover:text-yellow-300">{t("adminLogin")}</Link>
          <button
            onClick={toggleLanguage}
            className="bg-yellow-400 text-black px-3 py-1 rounded hover:bg-yellow-300"
          >
            üåê {lang === "en" ? "TA" : "EN"}
          </button>
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden">
          <button onClick={() => setIsOpen(!isOpen)}>
            {isOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>
      </div>

      {/* Mobile Dropdown */}
      {isOpen && (
        <div className="md:hidden mt-4 space-y-3 flex flex-col items-start">
          <Link to="/history" className="hover:text-yellow-300">{t("history")}</Link>
          <Link to="/events" className="hover:text-yellow-300">{t("events")}</Link>
          <Link to="/gallery" className="hover:text-yellow-300">{t("gallery")}</Link>
          <Link to="/workers" className="hover:text-yellow-300">{t("workers")}</Link>
          <Link to="/family-login" className="hover:text-yellow-300">{t("familyLogin")}</Link>
          <Link to="/admin-login" className="hover:text-yellow-300">{t("adminLogin")}</Link>
          <button
            onClick={toggleLanguage}
            className="bg-yellow-400 text-black px-3 py-1 rounded hover:bg-yellow-300"
          >
            üåê {lang === "en" ? "TA" : "EN"}
          </button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
