import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FamilyLogin = () => {
  const [familyId, setFamilyId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/families/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ familyId, password })
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem("familyToken", data.token);
        localStorage.setItem("familyId", data.family._id);
        navigate(`/family-dashboard`);
      } else {
        alert(data.message);
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="flex justify-center mt-10">
      <form onSubmit={handleLogin} className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-2xl font-bold mb-4">Family Login</h2>
        <input type="text" placeholder="Family ID" className="mb-4 w-full p-2 border rounded" value={familyId} onChange={(e)=>setFamilyId(e.target.value)} />
        <input type="password" placeholder="Password" className="mb-4 w-full p-2 border rounded" value={password} onChange={(e)=>setPassword(e.target.value)} />
        <button className="bg-green-700 text-white px-4 py-2 rounded">Login</button>
      </form>
    </div>
  );
};

export default FamilyLogin;
