import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [documents, setDocuments] = useState([]);
  const [file, setFile] = useState(null);

  const token = localStorage.getItem("familyToken");
  const familyId = localStorage.getItem("familyId"); // store on login

  // Fetch uploaded documents
  const fetchDocuments = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/family/documents/${familyId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDocuments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  // Upload file
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return alert("Please select a file");

    const formData = new FormData();
    formData.append("document", file);
    formData.append("familyId", familyId);

    try {
      await axios.post("http://localhost:5000/api/family/upload", formData, {
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" },
      });
      alert("File uploaded!");
      setFile(null);
      fetchDocuments();
    } catch (err) {
      console.error(err);
      alert("Upload failed");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Family Documents</h2>

      <form onSubmit={handleUpload} className="mb-4">
        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
        <button type="submit" className="ml-2 px-4 py-2 bg-green-600 text-white rounded">
          Upload
        </button>
      </form>

      <table className="w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">File Name</th>
            <th className="p-2">Uploaded At</th>
            <th className="p-2">Download</th>
          </tr>
        </thead>
        <tbody>
          {documents.map((doc) => (
            <tr key={doc.filename} className="border-t">
              <td className="p-2">{doc.originalName}</td>
              <td className="p-2">{new Date(doc.uploadedAt).toLocaleString()}</td>
              <td className="p-2">
                <a
                  href={`http://localhost:5000/api/family/documents/download/${familyId}/${doc.filename}`}
                  className="text-blue-600 underline"
                  target="_blank"
                  rel="noreferrer"
                >
                  Download
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FamilyDashboard;
