// Placeholder for client/src/components/Navbar.jsx
import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [lang, setLang] = useState("en");

  const toggleLanguage = () => {
    const newLang = lang === "en" ? "ta" : "en";
    i18n.changeLanguage(newLang);
    setLang(newLang);
  };

  return (
    <nav className="bg-green-700 text-white p-4 flex justify-between items-center">
      <div className="flex space-x-4">
        <Link to="/" className="hover:text-yellow-300">{t("home")}</Link>
        <Link to="/history" className="hover:text-yellow-300">{t("history")}</Link>
        <Link to="/events" className="hover:text-yellow-300">{t("events")}</Link>
        <Link to="/gallery" className="hover:text-yellow-300">{t("gallery")}</Link>
        <Link to="/workers" className="hover:text-yellow-300">{t("workers")}</Link>
      </div>
      <div className="flex space-x-4">
        <Link to="/family-login" className="hover:text-yellow-300">{t("familyLogin")}</Link>
        <Link to="/admin-login" className="hover:text-yellow-300">{t("adminLogin")}</Link>
        <button
          onClick={toggleLanguage}
          className="bg-yellow-400 text-black px-2 py-1 rounded"
        >
          üåê {lang === "en" ? "TA" : "EN"}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
