import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const token = localStorage.getItem("adminToken");

  useEffect(() => {
    const fetchFamilies = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/families", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamilies(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    const fetchEvents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/events", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEvents(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchFamilies();
    fetchEvents();
  }, [token]);

  const handleApprove = async (id, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId: id, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamilies(prev =>
        prev.map(f => (f._id === id ? { ...f, approved } : f))
      );
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/events/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(prev => prev.filter(e => e._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-8">
      <h2 className="text-3xl font-bold text-center mb-6">Admin Dashboard</h2>

      {/* Families Table */}
      <div>
        <h3 className="text-xl font-semibold mb-2">Families</h3>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-green-700 text-white">
              <th className="border p-2">Family ID</th>
              <th className="border p-2">Leader</th>
              <th className="border p-2">Approved</th>
              <th className="border p-2">Documents</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.map(f => (
              <tr key={f._id}>
                <td className="border p-2">{f.familyId}</td>
                <td className="border p-2">{f.leaderName}</td>
                <td className="border p-2">{f.approved ? "Yes" : "No"}</td>
                <td className="border p-2">
                  {f.documents && f.documents.length > 0 ? (
                    f.documents.map((doc, i) => (
                      <div key={i}>
                        <a
                          href={`http://localhost:5000/${doc.path}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 underline"
                        >
                          {doc.filename}
                        </a>
                      </div>
                    ))
                  ) : (
                    "No docs"
                  )}
                </td>
                <td className="border p-2 space-x-2">
                  {!f.approved && (
                    <button
                      onClick={() => handleApprove(f._id, true)}
                      className="bg-green-600 text-white px-2 py-1 rounded"
                    >
                      Approve
                    </button>
                  )}
                  {f.approved && (
                    <button
                      onClick={() => handleApprove(f._id, false)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Reject
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Events Table */}
      <div>
        <h3 className="text-xl font-semibold mb-2">Events</h3>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-purple-700 text-white">
              <th className="border p-2">Title</th>
              <th className="border p-2">Date</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map(e => (
              <tr key={e._id}>
                <td className="border p-2">{e.title}</td>
                <td className="border p-2">{new Date(e.date).toLocaleDateString()}</td>
                <td className="border p-2">
                  <button
                    onClick={() => handleDeleteEvent(e._id)}
                    className="bg-red-600 text-white px-2 py-1 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
