import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [modalData, setModalData] = useState({ type: "", mode: "", data: {} });
  const [modalVisible, setModalVisible] = useState(false);
  const token = localStorage.getItem("adminToken");

  // ------------------- Fetch All Data -------------------
  const fetchFamilies = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/families", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamilies(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/events", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(res.data);
    } catch (err) {
      setEvents([]);
    }
  };

  const fetchAchievements = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/achievements", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAchievements(res.data);
    } catch (err) {
      setAchievements([]);
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistory(res.data);
    } catch (err) {
      setHistory([]);
    }
  };

  useEffect(() => {
    if (token) {
      fetchFamilies();
      fetchEvents();
      fetchAchievements();
      fetchHistory();
    }
  }, [token]);

  // ------------------- Approve/Reject -------------------
  const handleApprove = async (id, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId: id, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamilies((prev) => prev.map((f) => (f._id === id ? { ...f, approved } : f)));
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  // ------------------- CRUD Delete -------------------
  const handleDeleteItem = async (type, id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/${type}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (type === "events") setEvents((prev) => prev.filter((e) => e._id !== id));
      if (type === "achievements") setAchievements((prev) => prev.filter((a) => a._id !== id));
      if (type === "history") setHistory((prev) => prev.filter((h) => h._id !== id));
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  // ------------------- Open Modal -------------------
  const openModal = (type, mode, data = {}) => {
    setModalData({ type, mode, data });
    setModalVisible(true);
  };

  // ------------------- Save Modal Data -------------------
  const handleModalSubmit = async (e) => {
    e.preventDefault();
    const { type, mode, data } = modalData;
    const url =
      mode === "edit"
        ? `http://localhost:5000/api/admin/${type}/${data._id}`
        : `http://localhost:5000/api/admin/${type}`;
    const method = mode === "edit" ? "put" : "post";

    try {
      const res = await axios({
        method,
        url,
        headers: { Authorization: `Bearer ${token}` },
        data: data,
      });
      if (type === "events") fetchEvents();
      if (type === "achievements") fetchAchievements();
      if (type === "history") fetchHistory();
      setModalVisible(false);
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  // ------------------- Modal Input Handler -------------------
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setModalData((prev) => ({ ...prev, data: { ...prev.data, [name]: value } }));
  };

  // ------------------- Render Table -------------------
  const renderTable = (items, type, headers) => (
    <div className="overflow-x-auto border rounded shadow">
      <h2 className="text-xl font-semibold p-4">{type.charAt(0).toUpperCase() + type.slice(1)}</h2>
      <button
        onClick={() => openModal(type, "add")}
        className="m-2 bg-green-600 text-white px-3 py-1 rounded"
      >
        Add New
      </button>
      <table className="w-full border-collapse text-center">
        <thead>
          <tr className="bg-gray-700 text-white">
            {headers.map((h, idx) => (
              <th key={idx} className="border p-2">{h}</th>
            ))}
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item._id}>
              {headers.map((h, idx) => (
                <td key={idx} className="border p-2">{item[h.toLowerCase()]}</td>
              ))}
              <td className="border p-2 space-x-2">
                <button
                  onClick={() => openModal(type, "edit", item)}
                  className="bg-blue-600 text-white px-2 py-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteItem(type, item._id)}
                  className="bg-red-600 text-white px-2 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-8">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Dashboard</h1>

      {/* Families Table */}
      <div className="overflow-x-auto border rounded shadow">
        <h2 className="text-xl font-semibold p-4">Families</h2>
        {/* Render families as before, including approve/reject and tax */}
      </div>

      {/* Render Events, Achievements, History */}
      {renderTable(events, "events", ["title", "description", "date"])}
      {renderTable(achievements, "achievements", ["title", "description"])}
      {renderTable(history, "history", ["title", "description"])}

      {/* ------------------- Modal ------------------- */}
      {modalVisible && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded shadow w-96">
            <h3 className="text-xl font-semibold mb-4">
              {modalData.mode === "add" ? "Add" : "Edit"} {modalData.type}
            </h3>
            <form onSubmit={handleModalSubmit} className="space-y-3">
              <input
                type="text"
                name="title"
                placeholder="Title"
                value={modalData.data.title || ""}
                onChange={handleInputChange}
                className="w-full border p-2 rounded"
                required
              />
              <textarea
                name="description"
                placeholder="Description"
                value={modalData.data.description || ""}
                onChange={handleInputChange}
                className="w-full border p-2 rounded"
                required
              />
              {modalData.type === "events" && (
                <input
                  type="date"
                  name="date"
                  value={modalData.data.date?.slice(0, 10) || ""}
                  onChange={handleInputChange}
                  className="w-full border p-2 rounded"
                  required
                />
              )}
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={() => setModalVisible(false)}
                  className="bg-gray-400 text-white px-3 py-1 rounded"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="bg-green-600 text-white px-3 py-1 rounded"
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
