import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = process.env.REACT_APP_API_URL || "https://uk-portal-3.onrender.com";
  const token = localStorage.getItem("familyToken");

  // Fetch family profile on mount or token change
  useEffect(() => {
    const fetchFamily = async () => {
      if (!token) {
        setMsg("‚ö†Ô∏è No authentication token found. Please login.");
        return;
      }
      setLoading(true);
      try {
        const res = await axios.get(`${API_BASE}/api/family/me`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamily(res.data);
      } catch (error) {
        console.error("Error fetching family profile:", error.response || error);
        setMsg(error.response?.data?.message || "‚ö†Ô∏è Failed to load family data. Please login again.");
      } finally {
        setLoading(false);
      }
    };
    fetchFamily();
  }, [token, API_BASE]);

  // Document upload handler
  const handleUpload = async () => {
    if (!file) {
      setMsg("‚ö†Ô∏è Please select a file");
      return;
    }
    setLoading(true);
    const formData = new FormData();
    formData.append("document", file);
    try {
      const res = await axios.post(`${API_BASE}/api/family/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      if (res.data.family) setFamily(res.data.family);
      setFile(null);
      setMsg("‚úÖ File uploaded successfully");
    } catch (error) {
      console.error("Upload error:", error.response || error);
      setMsg(error.response?.data?.message || "‚ö†Ô∏è Upload failed");
    } finally {
      setLoading(false);
    }
  };

  // Document delete handler
  const handleDelete = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;
    setLoading(true);
    try {
      const res = await axios.delete(`${API_BASE}/api/family/document/${docId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data.family) setFamily(res.data.family);
      setMsg("‚úÖ Document deleted successfully");
    } catch (error) {
      console.error("Delete error:", error.response || error);
      setMsg(error.response?.data?.message || "‚ö†Ô∏è Failed to delete document");
    } finally {
      setLoading(false);
    }
  };

  // Toggle Tax Paid Status handler
  const toggleTaxPaidStatus = async (taxId, currentStatus) => {
    setLoading(true);
    try {
      await axios.post(
        `${API_BASE}/api/family/tax`,    // Use POST /tax as per your API spec for update
        {
          taxId,                        // send taxId or familyId + month depends on your backend
          paid: !currentStatus,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const res = await axios.get(`${API_BASE}/api/family/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setMsg(`‚úÖ Tax marked as ${!currentStatus ? "Paid" : "Pending"}`);
    } catch (error) {
      console.error("Tax status toggle error:", error.response || error);
      setMsg(error.response?.data?.message || "‚ö†Ô∏è Failed to update tax status");
    } finally {
      setLoading(false);
    }
  };

  // Delete single tax entry handler
  const handleTaxDelete = async (taxId) => {
    if (!window.confirm("Are you sure you want to delete this tax record?")) return;
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/api/family/tax/${taxId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const res = await axios.get(`${API_BASE}/api/family/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setMsg("‚úÖ Tax record deleted successfully");
    } catch (error) {
      console.error("Delete tax error:", error.response || error);
      setMsg(error.response?.data?.message || "‚ö†Ô∏è Failed to delete tax record");
    } finally {
      setLoading(false);
    }
  };

  if (!family)
    return (
      <div className="p-8 text-center text-lg font-semibold animate-pulse text-gray-700">
        ‚è≥ Loading family data...
      </div>
    );

  return (
    <div className="p-4 sm:p-8 max-w-4xl mx-auto space-y-10 font-sans">
      <h2 className="text-3xl font-bold text-center text-green-700 mb-6">üè† Family Dashboard</h2>

      {loading && <p className="text-center font-semibold text-blue-600">Processing...</p>}
      {msg && <p className="text-center font-medium text-yellow-700">{msg}</p>}

      {/* Family Info Card */}
      <section className="bg-white rounded-lg shadow p-6">
        <h3 className="text-xl font-semibold border-b mb-4 pb-2 text-gray-800">Family Information</h3>
        <dl className="grid grid-cols-1 sm:grid-cols-2 gap-x-6 gap-y-4 text-gray-700 text-sm">
          <InfoItem label="Family ID" value={family.familyId} />
          <InfoItem label="Leader" value={family.leaderName} />
          <InfoItem label="Members" value={family.members?.join(", ") || "-"} />
          <InfoItem label="Address" value={family.address || "-"} />
          <InfoItem label="Email" value={family.email || "-"} />
          <InfoItem label="Phone" value={family.phone || "-"} />
        </dl>
      </section>

      {/* Tax Info Card */}
      <section className="bg-white rounded-lg shadow p-6 overflow-auto max-h-96">
        <h3 className="text-xl font-semibold border-b mb-4 pb-2 text-gray-800">Tax Information</h3>
        {family.taxHistory?.length ? (
          <table className="w-full border-collapse rounded-lg overflow-hidden border">
            <thead className="bg-gray-100 sticky top-0">
              <tr>
                {["Month", "Amount (‚Çπ)", "Status", "Action", "Delete"].map(title => (
                  <th key={title} className="p-3 border border-gray-300 text-center text-gray-700 font-semibold">{title}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {family.taxHistory.map((tax) => (
                <tr
                  key={tax._id}
                  className="hover:bg-gray-50 transition-colors"
                >
                  <td className="p-3 border border-gray-300 text-left">{tax.month}</td>
                  <td className="p-3 border border-gray-300 text-right">{tax.amount}</td>
                  <td className={`p-3 border border-gray-300 text-center font-bold ${tax.paid ? "text-green-600" : "text-red-600"}`}>{tax.paid ? "Paid" : "Pending"}</td>
                  <td className="p-3 border border-gray-300 text-center">
                    <button
                      onClick={() => toggleTaxPaidStatus(tax._id, tax.paid)}
                      disabled={loading}
                      className={`px-4 py-1 rounded focus:outline-none transition ${tax.paid ? "bg-yellow-500 hover:bg-yellow-600" : "bg-green-600 hover:bg-green-700 text-white"}`}
                      aria-label={`Mark tax for ${tax.month} as ${tax.paid ? "Pending" : "Paid"}`}
                    >
                      Mark as {tax.paid ? "Pending" : "Paid"}
                    </button>
                  </td>
                  <td className="p-3 border border-gray-300 text-center">
                    <button
                      onClick={() => handleTaxDelete(tax._id)}
                      disabled={loading}
                      className="bg-red-600 hover:bg-red-700 text-white px-4 py-1 rounded focus:outline-none"
                      aria-label={`Delete tax record for ${tax.month}`}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-600">No tax records found.</p>
        )}
      </section>

      {/* Document Upload */}
      <section className="bg-white rounded-lg shadow p-6">
        <h3 className="text-xl font-semibold border-b mb-4 pb-2 text-gray-800">Upload Documents</h3>
        <div className="flex flex-col sm:flex-row gap-4 items-center">
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            aria-label="Select document file"
            className="border border-gray-300 rounded px-3 py-2 w-full sm:w-auto"
          />
          <button
            onClick={handleUpload}
            disabled={loading || !file}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 focus:outline-none transition"
          >
            Upload
          </button>
        </div>
      </section>

      {/* Uploaded Documents */}
      <section className="bg-white rounded-lg shadow p-6">
        <h3 className="text-xl font-semibold border-b mb-4 pb-2 text-gray-800">Uploaded Documents</h3>
        {family.documents?.length ? (
          <ul className="space-y-3">
            {family.documents.map(doc => (
              <li
                key={doc._id}
                className="flex flex-col sm:flex-row justify-between items-center bg-gray-50 rounded px-3 py-2"
              >
                <span className="truncate max-w-xs">{doc.originalName || doc.filename}</span>
                <span className="text-gray-500 text-xs">{new Date(doc.uploadedAt).toLocaleString()}</span>
                <div className="flex gap-4 mt-2 sm:mt-0">
                  <a
                    href={`${API_BASE}${doc.path}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    download
                    className="text-blue-600 hover:underline"
                    aria-label={`Download ${doc.originalName || doc.filename}`}
                  >
                    Download
                  </a>
                  <button
                    onClick={() => handleDelete(doc._id)}
                    disabled={loading}
                    className="text-red-600 hover:underline focus:outline-none"
                    aria-label={`Delete ${doc.originalName || doc.filename}`}
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No documents uploaded yet.</p>
        )}
      </section>
    </div>
  );
};

export default FamilyDashboard;
