import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const token = localStorage.getItem("familyToken"); // stored on login

  // Fetch logged-in family info
  useEffect(() => {
    const fetchFamily = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/family/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamily(res.data);
      } catch (err) {
        console.error("Error fetching family:", err.response?.data || err.message);
      }
    };
    fetchFamily();
  }, [token]);

  // Handle file selection
  const handleFileChange = (e) => setFile(e.target.files[0]);

  // Upload selected file
  const handleUpload = async () => {
    if (!file) return alert("Please select a file to upload");

    const formData = new FormData();
    formData.append("document", file);
    formData.append("familyId", family.familyId);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/family/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setUploadMessage("File uploaded successfully!");
      setFile(null);

      // Refresh family info to show updated documents
      const updatedFamily = await axios.get(
        "http://localhost:5000/api/family/me",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamily(updatedFamily.data);
    } catch (err) {
      console.error("Upload error:", err.response?.data || err.message);
      setUploadMessage("File upload failed. Try again.");
    }
  };

  if (!family) return <p className="p-6 text-center">Loading family data...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-2xl font-bold text-center">👨‍👩‍👧‍👦 Family Dashboard</h2>

      {/* Family Info Table */}
      <table className="w-full border border-gray-300 rounded mb-6">
        <tbody>
          <tr>
            <td className="p-2 font-semibold">Family ID</td>
            <td className="p-2">{family.familyId}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Leader Name</td>
            <td className="p-2">{family.leaderName}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Members</td>
            <td className="p-2">{family.members?.join(", ") || "N/A"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Address</td>
            <td className="p-2">{family.address || "N/A"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Phone</td>
            <td className="p-2">{family.phone || "N/A"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Email</td>
            <td className="p-2">{family.email || "N/A"}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Current Tax</td>
            <td className="p-2">
              {family.taxHistory?.length > 0
                ? family.taxHistory[family.taxHistory.length - 1].amount
                : 0}{" "}
              INR
            </td>
          </tr>
        </tbody>
      </table>

      {/* Document Upload */}
      <div className="mb-6">
        <h3 className="font-semibold mb-2">Upload Documents</h3>
        <input type="file" onChange={handleFileChange} />
        <button
          onClick={handleUpload}
          className="ml-2 px-4 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Upload
        </button>
        {uploadMessage && <p className="mt-2 text-green-700">{uploadMessage}</p>}
      </div>

      {/* Uploaded Documents */}
      <div>
        <h3 className="font-semibold mb-2">Uploaded Documents</h3>
        <ul className="list-disc pl-5">
          {family.documents && family.documents.length > 0 ? (
            family.documents.map((doc, index) => (
              <li key={index} className="mb-1">
                <a
                  href={`http://localhost:5000/${doc.path.replace("\\", "/")}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 underline"
                >
                  {doc.originalName}
                </a>{" "}
                - {new Date(doc.uploadedAt).toLocaleDateString()}
              </li>
            ))
          ) : (
            <li>No documents uploaded yet.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default FamilyDashboard;
