import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = process.env.REACT_APP_API_URL || "https://uk-portal-3.onrender.com";
  const token = localStorage.getItem("familyToken");

  // Fetch family profile
  useEffect(() => {
    const fetchFamily = async () => {
      if (!token) {
        setMsg("‚ö†Ô∏è No authentication token found. Please login.");
        return;
      }
      setLoading(true);
      try {
        const res = await axios.get(`${API_BASE}/api/family/me`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamily(res.data);
      } catch (err) {
        console.error("Error fetching family profile:", err.response || err);
        setMsg(err.response?.data?.message || "‚ö†Ô∏è Failed to load family data. Please login again.");
      } finally {
        setLoading(false);
      }
    };
    fetchFamily();
  }, [token, API_BASE]);

  // Handle document upload
  const handleUpload = async () => {
    if (!file) {
      setMsg("‚ö†Ô∏è Please select a file");
      return;
    }
    setLoading(true);
    const formData = new FormData();
    formData.append("document", file);
    try {
      const res = await axios.post(`${API_BASE}/api/family/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      if (res.data.family) setFamily(res.data.family);
      setFile(null);
      setMsg("‚úÖ File uploaded successfully");
    } catch (err) {
      console.error("Upload error:", err.response || err);
      setMsg(err.response?.data?.message || "‚ö†Ô∏è Upload failed");
    } finally {
      setLoading(false);
    }
  };

  // Handle document delete
  const handleDelete = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;

    setLoading(true);
    try {
      const res = await axios.delete(`${API_BASE}/api/family/document/${docId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data.family) setFamily(res.data.family);
      setMsg("‚úÖ Document deleted successfully");
    } catch (err) {
      console.error("Delete error:", err.response || err);
      setMsg(err.response?.data?.message || "‚ö†Ô∏è Failed to delete document");
    } finally {
      setLoading(false);
    }
  };

  // Handle Tax status toggle (mark paid/unpaid)
  const toggleTaxPaidStatus = async (taxId, currentStatus) => {
    if (!token) return setMsg("‚ö†Ô∏è Missing auth token");
    setLoading(true);
    try {
      await axios.patch(
        `${API_BASE}/api/family/tax/${taxId}/togglePaid`,
        { paid: !currentStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const res = await axios.get(`${API_BASE}/api/family/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setMsg(`‚úÖ Tax marked as ${!currentStatus ? "Paid" : "Pending"}`);
    } catch (err) {
      console.error("Tax status toggle error:", err.response || err);
      setMsg(err.response?.data?.message || "‚ö†Ô∏è Failed to update tax status");
    } finally {
      setLoading(false);
    }
  };

  // Handle Delete single tax entry
  const handleTaxDelete = async (taxId) => {
    if (!window.confirm("Are you sure you want to delete this tax record?")) return;

    if (!token) return setMsg("‚ö†Ô∏è Missing auth token");
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/api/family/tax/${taxId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const res = await axios.get(`${API_BASE}/api/family/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setMsg("‚úÖ Tax record deleted successfully");
    } catch (err) {
      console.error("Delete tax error:", err.response || err);
      setMsg(err.response?.data?.message || "‚ö†Ô∏è Failed to delete tax record");
    } finally {
      setLoading(false);
    }
  };

  if (!family)
    return (
      <div className="p-8 text-center text-lg font-semibold animate-pulse">
        ‚è≥ Loading family data...
      </div>
    );

  return (
    <div className="p-4 sm:p-6 md:p-8 max-w-5xl mx-auto space-y-8">
      <h2 className="text-3xl font-bold text-center mb-2 text-green-700">üè† Family Dashboard</h2>

      {loading && <p className="text-center text-blue-600 font-semibold">Processing...</p>}
      {msg && <p className="text-center text-yellow-700 font-medium">{msg}</p>}

      {/* Family Info Card */}
      <section className="bg-white shadow rounded-lg p-6">
        <h3 className="text-xl font-semibold mb-4 border-b pb-2">Family Information</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-y-3 gap-x-6 text-gray-700">
          <Item label="Family ID" value={family.familyId} />
          <Item label="Leader" value={family.leaderName} />
          <Item label="Members" value={family.members?.join(", ") || "-"} />
          <Item label="Address" value={family.address || "-"} />
          <Item label="Email" value={family.email || "-"} />
          <Item label="Phone" value={family.phone || "-"} />
        </div>
      </section>

      {/* Tax Info Card */}
      <section className="bg-white shadow rounded-lg p-6 overflow-x-auto">
        <h3 className="text-xl font-semibold mb-4 border-b pb-2">Tax Information</h3>
        {family.taxHistory?.length > 0 ? (
          <table className="min-w-full border-collapse border border-gray-300 rounded-lg">
            <thead className="bg-gray-100">
              <tr>
                <th className="border border-gray-300 p-3 text-left">Month</th>
                <th className="border border-gray-300 p-3 text-right">Amount (INR)</th>
                <th className="border border-gray-300 p-3 text-center">Status</th>
                <th className="border border-gray-300 p-3 text-center">Admin Action</th>
                <th className="border border-gray-300 p-3 text-center">Delete</th>
              </tr>
            </thead>
            <tbody>
              {family.taxHistory.map((tax) => (
                <tr
                  key={tax._id}
                  className="even:bg-gray-50 hover:bg-gray-100 transition-colors"
                >
                  <td className="border border-gray-300 p-3">{tax.month}</td>
                  <td className="border border-gray-300 p-3 text-right">{tax.amount}</td>
                  <td
                    className={`border border-gray-300 p-3 text-center font-bold ${
                      tax.paid ? "text-green-600" : "text-red-600"
                    }`}
                  >
                    {tax.paid ? "Paid" : "Pending"}
                  </td>
                  <td className="border border-gray-300 p-3 text-center">
                    <button
                      className={`px-4 py-1 rounded focus:outline-none transition ${
                        tax.paid ? "bg-yellow-500 hover:bg-yellow-600" : "bg-green-600 text-white hover:bg-green-700"
                      }`}
                      onClick={() => toggleTaxPaidStatus(tax._id, tax.paid)}
                      disabled={loading}
                      aria-label={`Mark tax for ${tax.month} as ${tax.paid ? "Pending" : "Paid"}`}
                    >
                      Mark as {tax.paid ? "Pending" : "Paid"}
                    </button>
                  </td>
                  <td className="border border-gray-300 p-3 text-center">
                    <button
                      className="bg-red-600 text-white px-4 py-1 rounded hover:bg-red-700 transition focus:outline-none"
                      onClick={() => handleTaxDelete(tax._id)}
                      disabled={loading}
                      aria-label={`Delete tax record for ${tax.month}`}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-600">No tax records found.</p>
        )}
      </section>

      {/* Document Upload Card */}
      <section className="bg-white shadow rounded-lg p-6">
        <h3 className="text-xl font-semibold mb-4 border-b pb-2">Upload Document</h3>
        <div className="flex flex-col sm:flex-row sm:items-center gap-3">
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            className="border border-gray-300 rounded px-3 py-2 w-full sm:w-auto"
            aria-label="Select document file"
          />
          <button
            onClick={handleUpload}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition focus:outline-none"
            disabled={loading || !file}
          >
            Upload
          </button>
        </div>
      </section>

      {/* Uploaded Documents Card */}
      <section className="bg-white shadow rounded-lg p-6">
        <h3 className="text-xl font-semibold mb-4 border-b pb-2">Uploaded Documents</h3>
        {family.documents?.length > 0 ? (
          <ul className="space-y-3">
            {family.documents.map((doc) => (
              <li
                key={doc._id}
                className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-2 sm:gap-0"
              >
                <div className="flex flex-col sm:flex-row sm:items-center gap-2">
                  <span className="font-medium truncate max-w-xs">{doc.originalName || doc.filename}</span>
                  <span className="text-gray-500 text-sm">{new Date(doc.uploadedAt).toLocaleDateString()}</span>
                </div>
                <div className="flex gap-4">
                  <a
                    href={`${API_BASE}${doc.path}`}
                    target="_blank"
                    rel="noreferrer"
                    className="text-blue-600 underline whitespace-nowrap"
                    download
                    aria-label={`Download ${doc.originalName || doc.filename}`}
                  >
                    Download
                  </a>
                  <button
                    onClick={() => handleDelete(doc._id)}
                    className="text-red-600 underline whitespace-nowrap"
                    disabled={loading}
                    aria-label={`Delete ${doc.originalName || doc.filename}`}
                  >
                    Clear
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No documents uploaded.</p>
        )}
      </section>
    </div>
  );
};

const Item = ({ label, value }) => (
  <>
    <dt className="font-semibold">{label}:</dt>
    <dd className="text-gray-700">{value}</dd>
  </>
);

export default FamilyDashboard;
