import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
  const token = localStorage.getItem("adminToken");
  const headers = { Authorization: `Bearer ${token}` };

  // Core data states
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [historyItems, setHistoryItems] = useState([]);
  const [gallery, setGallery] = useState([]);

  // UI states
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  // Form states
  const [newFamily, setNewFamily] = useState({
    familyId: "",
    leaderName: "",
    email: "",
    password: "",
    members: "",
    phone: "",
    address: "",
  });

  const [newEvent, setNewEvent] = useState({ title: "", date: "" });
  const [newWorker, setNewWorker] = useState({ type: "", description: "" });
  const [newHistory, setNewHistory] = useState({ contentEN: "", contentTA: "" });
  const [galleryFile, setGalleryFile] = useState(null);
  const [galleryMeta, setGalleryMeta] = useState({ title: "", description: "" });

  // Tax related states
  const [selectedFamilies, setSelectedFamilies] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [taxMonth, setTaxMonth] = useState(() => {
    const d = new Date();
    return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
  });
  const [taxAmount, setTaxAmount] = useState("");
  const [taxPaid, setTaxPaid] = useState(false);

  const [searchFamily, setSearchFamily] = useState("");

  // Load all data
  const loadAll = async () => {
    if (!token) {
      setErrorMsg("Missing admin token. Please login.");
      return;
    }
    try {
      setLoading(true);
      const [fRes, eRes, wRes, hRes, gRes] = await Promise.all([
        axios.get(`${API_BASE}/api/admin/families`, { headers }),
        axios.get(`${API_BASE}/api/admin/events`, { headers }),
        axios.get(`${API_BASE}/api/admin/workers`, { headers }),
        axios.get(`${API_BASE}/api/admin/history`, { headers }),
        axios.get(`${API_BASE}/api/admin/gallery`, { headers }),
      ]);
      setFamilies(fRes.data || []);
      setEvents(eRes.data || []);
      setWorkers(wRes.data || []);
      setHistoryItems(hRes.data || []);
      setGallery(gRes.data || []);
      setErrorMsg("");
    } catch (error) {
      setErrorMsg("Failed to load data. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAll();
  }, []);

  // Family CRUD, Tax ops, Event, Worker, History, Gallery functions...
  // (Use previously provided implementations for brevity)

  // Filtering
  const filteredFamilies = useMemo(() => {
    if (!searchFamily.trim()) return families;
    const l = searchFamily.toLowerCase();
    return families.filter(f =>
      (f.familyId + ' ' + f.leaderName + ' ' + (f.email ?? '')).toLowerCase().includes(l));
  }, [families, searchFamily]);

  const paidFamilies = useMemo(() => families.filter(f => f.taxHistory?.some(t => t.month === taxMonth && t.paid)), [families, taxMonth]);
  const unpaidFamilies = useMemo(() => families.filter(f => {
    const tx = f.taxHistory?.find(t => t.month === taxMonth);
    return !tx || !tx.paid;
  }), [families, taxMonth]);
  const totalPaid = useMemo(() => paidFamilies.reduce((acc, f) => {
    const tx = f.taxHistory?.find(t => t.month === taxMonth && t.paid);
    return acc + (Number(tx?.amount) || 0);
  }, 0), [paidFamilies, taxMonth]);

  // Wrap with your implementations for create, approve, delete, toggle etc.

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-10">

      <h1 className="text-4xl font-bold text-center mb-8">Admin Dashboard</h1>

      {errorMsg && <div className="text-center text-red-600">{errorMsg}</div>}
      {loading && <div className="text-center text-blue-600">Loading...</div>}

      {/* Families and Create */}
      <section className="grid md:grid-cols-3 gap-8">
        <div className="md:col-span-2 bg-white p-6 rounded shadow flex flex-col">

          <div className="flex flex-wrap items-center justify-between mb-4 gap-3">
            <input type="search" placeholder="Search families..." value={searchFamily} onChange={e => setSearchFamily(e.target.value)}
              className="border p-2 rounded w-full md:max-w-sm" />
            <div>
              <button onClick={loadAll} className="btn-secondary mr-2">Refresh</button>
              <button onClick={() => { if(window.confirm("Send notifications to all families?")) alert("Notifications sent (backend)") }} className="btn-yellow">Send Notifications</button>
            </div>
          </div>

          <div className="overflow-auto max-h-96 border rounded">
            <table className="min-w-full table-auto border-collapse">
              <thead className="sticky top-0 bg-gray-100 font-semibold">
                <tr>
                  <th className="p-3 border w-12 text-center">
                    <input type="checkbox" checked={selectAll} onChange={() => setSelectAll(!selectAll)} onClick={() => selectAll ? setSelectedFamilies([]) : setSelectedFamilies(families.map(f => f._id))} />
                  </th>
                  <th className="p-3 border">ID</th>
                  <th className="p-3 border">Leader</th>
                  <th className="p-3 border text-center">Approved</th>
                  <th className="p-3 border">Latest Tax</th>
                  <th className="p-3 border text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredFamilies.length ? filteredFamilies.map(f => {
                  const latestTax = f.taxHistory?.slice(-1)[0];
                  return (
                    <tr key={f._id} className="hover:bg-gray-50">
                      <td className="p-2 border text-center">
                        <input type="checkbox" checked={selectedFamilies.includes(f._id)} onChange={() => {
                          if (selectedFamilies.includes(f._id)) setSelectedFamilies(selectedFamilies.filter(sid => sid !== f._id));
                          else setSelectedFamilies([...selectedFamilies, f._id]);
                        }}/>
                      </td>
                      <td className="p-2 border">{f.familyId}</td>
                      <td className="p-2 border">{f.leaderName}</td>
                      <td className="p-2 border text-center">{f.approved ? "Yes" : "No"}</td>
                      <td className="p-2 border">{latestTax ? `${latestTax.month} - ₹${latestTax.amount} (${latestTax.paid ? "Paid" : "Pending"})` : "No Tax"}</td>
                      <td className="p-2 border space-x-2 text-center">
                        <button className="btn-green btn-small" disabled={loading} onClick={() => approveFamily(f._id, true)}>Approve</button>
                        <button className="btn-red btn-small" disabled={loading} onClick={() => approveFamily(f._id, false)}>Reject</button>
                      </td>
                    </tr>
                  );
                }) : <tr><td colSpan={6} className="p-3 text-center text-gray-500">No families found</td></tr>}
              </tbody>
            </table>
          </div>
        </div>

        <div className="bg-white p-6 rounded shadow flex flex-col gap-3 sticky top-6">
          <h2 className="text-xl font-semibold mb-3">Create Family</h2>
          <input className="input" placeholder="Family ID" value={newFamily.familyId} onChange={e => setNewFamily({...newFamily, familyId: e.target.value})} />
          <input className="input" placeholder="Leader Name" value={newFamily.leaderName} onChange={e => setNewFamily({...newFamily, leaderName: e.target.value})} />
          <input className="input" placeholder="Email" value={newFamily.email} onChange={e => setNewFamily({...newFamily, email: e.target.value})} />
          <input className="input" type="password" placeholder="Password" value={newFamily.password} onChange={e => setNewFamily({...newFamily, password: e.target.value})} />
          <input className="input" placeholder="Members (comma separated)" value={newFamily.members} onChange={e => setNewFamily({...newFamily, members: e.target.value})} />
          <input className="input" placeholder="Phone" value={newFamily.phone} onChange={e => setNewFamily({...newFamily, phone: e.target.value})} />
          <input className="input" placeholder="Address" value={newFamily.address} onChange={e => setNewFamily({...newFamily, address: e.target.value})} />
          <button className="btn-blue mt-2" disabled={loading} onClick={createFamily}>Create</button>
        </div>
      </section>

      <section className="grid md:grid-cols-3 gap-6 mt-10">
        <article className="bg-white p-6 rounded shadow space-y-3">
          <h3 className="text-lg font-semibold">Add Tax (Bulk)</h3>
          <div className="space-y-2">
            <label className="flex items-center gap-2"><input type="checkbox" checked={selectAll} onChange={toggleSelectAll} /> Select All Families</label>
            <input type="month" className="input" value={taxMonth} onChange={e => setTaxMonth(e.target.value)} />
            <input type="number" className="input" placeholder="Amount" value={taxAmount} onChange={e => setTaxAmount(e.target.value)} />
            <label className="flex items-center gap-2"><input type="checkbox" checked={taxPaid} onChange={e => setTaxPaid(e.target.checked)} /> Mark as Paid</label>
            <button className="btn-green w-full" disabled={loading} onClick={addTaxBulk}>Add</button>
            <p className="pt-2 text-center font-semibold">Total Paid: ₹{totalPaid}</p>
          </div>
        </article>

        <article className="bg-white p-6 rounded shadow max-h-[400px] overflow-auto">
          <h3 className="text-lg font-semibold mb-3">Paid Families ({paidFamilies.length})</h3>
          {paidFamilies.length ? paidFamilies.map(f => {
            const t = f.taxHistory.find(tx => tx.month === taxMonth && tx.paid);
            return (
              <div key={f._id} className="bg-green-50 p-2 rounded mb-2 flex justify-between">
                <span>{f.familyId} — {f.leaderName}</span>
                <strong>₹{t?.amount}</strong>
              </div>
            );
          }) : <p>No paid families</p>}
        </article>

        <article className="bg-white p-6 rounded shadow max-h-[400px] overflow-auto">
          <h3 className="text-lg font-semibold mb-3">Pending Families ({unpaidFamilies.length})</h3>
          {unpaidFamilies.length ? unpaidFamilies.map(f => {
            const t = f.taxHistory?.find(tx => tx.month === taxMonth);
            const amount = t ? t.amount : taxAmount || "-";
            return (
              <div key={f._id} className="bg-red-50 p-2 rounded mb-2 flex justify-between">
                <span>{f.familyId} — {f.leaderName} • ₹{amount}</span>
                <button className="btn-blue" disabled={loading} onClick={() => markFamilyTax(f._id, taxMonth)}>Mark Paid</button>
              </div>
            );
          }) : <p>No pending families</p>}
        </article>
      </section>

      {/* Sections for Events, Workers, History, Gallery */}

      <section className="grid md:grid-cols-2 gap-6 mt-10">

        <article className="bg-white p-6 rounded shadow">
          <h3 className="text-lg font-semibold mb-4">Events</h3>
          <ul className="space-y-2 max-h-64 overflow-auto">
            {events.map(ev => (
              <li key={ev._id} className="flex justify-between items-center border-b py-2">
                <span>{ev.title} — {new Date(ev.date).toLocaleDateString()}</span>
                <button className="btn-red btn-small" onClick={() => deleteEvent(ev._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="mt-4 grid grid-cols-1 sm:grid-cols-3 gap-2">
            <input className="input" placeholder="Title" value={newEvent.title} onChange={e => setNewEvent({...newEvent, title: e.target.value})} />
            <input className="input" type="date" value={newEvent.date} onChange={e => setNewEvent({...newEvent, date: e.target.value})} />
            <button className="btn-green" onClick={addEvent}>Add</button>
          </div>
        </article>

        <article className="bg-white p-6 rounded shadow">
          <h3 className="text-lg font-semibold mb-4">Workers</h3>
          <ul className="space-y-2 max-h-64 overflow-auto">
            {workers.map(w => (
              <li key={w._id} className="flex justify-between items-center border-b py-2">
                <span>{w.type} — {w.description}</span>
                <button className="btn-red btn-small" onClick={() => deleteWorker(w._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="mt-4 grid grid-cols-1 gap-2">
            <input className="input" placeholder="Type" value={newWorker.type} onChange={e => setNewWorker({...newWorker, type: e.target.value})} />
            <input className="input" placeholder="Description" value={newWorker.description} onChange={e => setNewWorker({...newWorker, description: e.target.value})} />
            <button className="btn-green" onClick={addWorker}>Add</button>
          </div>
        </article>

        <article className="bg-white p-6 rounded shadow md:col-span-2">
          <h3 className="text-lg font-semibold mb-4">History</h3>
          <ul className="space-y-2 max-h-64 overflow-auto">
            {historyItems.map(h => (
              <li key={h._id} className="flex justify-between items-center border-b py-2">
                <span>{h.content?.en || h.content?.ta}</span>
                <button className="btn-red btn-small" onClick={() => deleteHistory(h._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="mt-4 grid grid-cols-1 sm:grid-cols-3 gap-2">
            <input className="input" placeholder="Content (English)" value={newHistory.contentEN} onChange={e => setNewHistory({...newHistory, contentEN: e.target.value})} />
            <input className="input" placeholder="Content (Other)" value={newHistory.contentTA} onChange={e => setNewHistory({...newHistory, contentTA: e.target.value})} />
            <button className="btn-green" onClick={addHistory}>Add</button>
          </div>
        </article>
      </section>

      <section className="bg-white p-6 rounded shadow mt-10">
        <h3 className="text-lg font-semibold mb-4">Gallery</h3>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          {gallery.map(item => (
            <div key={item._id} className="border rounded p-2 flex flex-col">
              <a href={`${API_BASE}${item.url}`} target="_blank" rel="noreferrer" className="hover:underline text-blue-600 truncate">{item.title || "Untitled"}</a>
              <p className="text-gray-600 text-sm mb-2 truncate">{item.description || ""}</p>
              <button className="btn-red btn-small mt-auto" onClick={() => deleteGallery(item._id)}>Delete</button>
            </div>
          ))}
        </div>
        <div className="mt-4 grid grid-cols-1 sm:grid-cols-3 gap-2">
          <input type="file" onChange={e => setGalleryFile(e.target.files[0])} />
          <input placeholder="Title" className="input" value={galleryMeta.title} onChange={e => setGalleryMeta({...galleryMeta, title: e.target.value})} />
          <input placeholder="Description" className="input" value={galleryMeta.description} onChange={e => setGalleryMeta({...galleryMeta, description: e.target.value})} />
          <button className="btn-green" onClick={uploadGallery} disabled={!galleryFile}>Upload</button>
        </div>
      </section>

    </div>
  );
};

export default AdminDashboard;
