// Placeholder for client/src/pages/Gallery.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import GalleryModal from "../components/GalleryModal";

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    axios.get("/api/village/gallery")
      .then(res => setImages(res.data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">Photo Gallery</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {images.map((img, idx) => (
          <img
            key={idx}
            src={img.url}
            alt={`Gallery ${idx}`}
            className="cursor-pointer rounded shadow hover:scale-105 transition"
            onClick={() => setSelectedImage(img.url)}
          />
        ))}
      </div>

      <GalleryModal image={selectedImage} onClose={() => setSelectedImage(null)} />
    </div>
  );
};

export default Gallery;
