// client/src/pages/Home.jsx
import React, { useRef } from "react";
import { useTranslation } from "react-i18next";
import GoogleMap from "./GoogleMap";
import "../styles.css"; // import styles
import Footer from "../components/Footer";

const Home = () => {
  const { t } = useTranslation();

  // refs for sections
  const aboutRef = useRef(null);
  const galleryRef = useRef(null);
  const eventsRef = useRef(null);

  // scroll function
  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="home-container">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">{t("welcome")}</h1>
          <p className="hero-subtitle">{t("explain")}</p>
          <button
            className="hero-button"
            onClick={() => scrollToSection(aboutRef)}
          >
            🌿 Explore More
          </button>
        </div>
      </section>

      {/* About Section */}
      <section className="about-section" ref={aboutRef}>
        <h2 className="section-title">🌸 {t("about")}</h2>
        <p className="section-text">{t("value")}</p>
      </section>

      {/* Gallery Section */}
      <section className="gallery-section" ref={galleryRef}>
        <h2 className="section-title">🖼️ {t("gallery")}</h2>
        <div className="gallery-grid">
          <div className="gallery-item">🌄 Image 1</div>
          <div className="gallery-item">🌅 Image 2</div>
          <div className="gallery-item">🏞️ Image 3</div>
        </div>
        <button
          className="hero-button mt-4"
          onClick={() => scrollToSection(eventsRef)}
        >
          👉 Next: Events
        </button>
      </section>

      {/* Events Section */}
      <section className="events-section" ref={eventsRef}>
        <h2 className="section-title">🎉 {t("events")}</h2>
        <ul className="section-text">
          <li>🌾 Pongal Festival</li>
          <li>🎶 Cultural Night</li>
          <li>🌍 Environmental Day</li>
        </ul>
      </section>

      {/* Google Map Section */}
      <section className="map-section">
        <h2 className="section-title">📍 {t("findUsOnMap")}</h2>
        <div className="map-container">
          <GoogleMap />
        </div>
      </section>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Home;
