import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // Ensure this key matches what you stored in login
  const token = localStorage.getItem("adminToken");

  // -------------------
  // Fetch Families
  // -------------------
  const fetchFamilies = async () => {
    if (!token) return;
    try {
      const res = await axios.get("http://localhost:5000/api/admin/families", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamilies(res.data);
    } catch (err) {
      console.error("Error fetching families:", err.response?.data || err.message);
      setError("Failed to load families.");
    }
  };

  // -------------------
  // Fetch Events
  // -------------------
  const fetchEvents = async () => {
    if (!token) return;
    try {
      const res = await axios.get("http://localhost:5000/api/admin/events", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(res.data);
    } catch (err) {
      console.warn("No events found or error fetching:", err.response?.data || err.message);
      setEvents([]);
    }
  };

  useEffect(() => {
    if (token) {
      fetchFamilies();
      fetchEvents();
    }
  }, [token]);

  // -------------------
  // Approve / Reject Family
  // -------------------
  const handleApprove = async (familyId, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamilies((prev) =>
        prev.map((f) => (f._id === familyId ? { ...f, approved } : f))
      );
    } catch (err) {
      console.error("Error updating approval:", err.response?.data || err.message);
    }
  };

  // -------------------
  // Delete Event
  // -------------------
  const handleDeleteEvent = async (eventId) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/events/${eventId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents((prev) => prev.filter((e) => e._id !== eventId));
    } catch (err) {
      console.error("Error deleting event:", err.response?.data || err.message);
    }
  };

  // -------------------
  // Send Tax Reminders
  // -------------------
  const handleSendReminders = async () => {
    setLoading(true);
    setMessage("");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/send-tax-reminders",
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message);
    } catch (err) {
      console.error("Error sending reminders:", err.response?.data || err.message);
      setMessage("Error sending reminders");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>

      {/* Tax Notifications */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Tax Notifications</h2>
        <button
          onClick={handleSendReminders}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={loading}
        >
          {loading ? "Sending..." : "Send Tax Reminders (Email/SMS)"}
        </button>
        {message && <p className="mt-2 text-green-700">{message}</p>}
      </div>

      {/* Families Table */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Families</h2>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-green-700 text-white">
              <th className="border p-2">Family ID</th>
              <th className="border p-2">Leader Name</th>
              <th className="border p-2">Address</th>
              <th className="border p-2">Approved</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.length > 0 ? (
              families.map((f) => (
                <tr key={f._id}>
                  <td className="border p-2">{f.familyId}</td>
                  <td className="border p-2">{f.leaderName}</td>
                  <td className="border p-2">{f.address}</td>
                  <td className="border p-2">{f.approved ? "Yes" : "No"}</td>
                  <td className="border p-2 space-x-2">
                    {!f.approved && (
                      <button
                        onClick={() => handleApprove(f._id, true)}
                        className="bg-green-600 text-white px-2 py-1 rounded"
                      >
                        Approve
                      </button>
                    )}
                    {f.approved && (
                      <button
                        onClick={() => handleApprove(f._id, false)}
                        className="bg-red-600 text-white px-2 py-1 rounded"
                      >
                        Reject
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="p-4 text-center text-gray-500">
                  No families found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Events Table */}
      {events.length > 0 && (
        <div className="card p-4 border rounded shadow">
          <h2 className="text-xl font-semibold mb-2">Events</h2>
          <table className="w-full border-collapse border text-center">
            <thead>
              <tr className="bg-purple-700 text-white">
                <th className="border p-2">Title</th>
                <th className="border p-2">Description</th>
                <th className="border p-2">Date</th>
                <th className="border p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {events.map((e) => (
                <tr key={e._id}>
                  <td className="border p-2">{e.title}</td>
                  <td className="border p-2">{e.description}</td>
                  <td className="border p-2">{new Date(e.date).toLocaleDateString()}</td>
                  <td className="border p-2">
                    <button
                      onClick={() => handleDeleteEvent(e._id)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
