import React, { useEffect, useRef } from "react";

const GoogleMap = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const loadMap = () => {
      const udaikulam = { lat: 9.9215, lng: 78.1197 };

      const map = new window.google.maps.Map(mapRef.current, {
        center: udaikulam,
        zoom: 15,
      });

      // Use AdvancedMarkerElement (recommended)
      new window.google.maps.marker.AdvancedMarkerElement({
        position: udaikulam,
        map,
        title: "Udaikulam Village",
      });
    };

    if (!window.google || !window.google.maps) {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=AQ.Ab8RN6IvE7ZcBnL1WbZoxodlXDXIAHpOuQiXVvz7vZLBOTkOMw`;
      script.async = true;
      script.defer = true;
      document.body.appendChild(script);

      script.onload = loadMap;
    } else {
      loadMap();
    }
  }, []);

  return <div ref={mapRef} className="w-full h-96 rounded shadow-lg"></div>;
};

export default GoogleMap;
