// FamilyDashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import DocumentCard from "../components/DocumentCard";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  const token = localStorage.getItem("FAMILY_TOKEN");

  // ðŸ“Œ Fetch family details
  const fetchFamilyData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/family/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
    } catch (err) {
      console.error("Error fetching family details:", err);
    }
  };

  // ðŸ“Œ Fetch family documents
  const fetchDocuments = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/family/documents", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDocuments(res.data);
    } catch (err) {
      console.error("Error fetching documents:", err);
    }
  };

  // ðŸ“Œ Handle file upload
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append("document", selectedFile);

    try {
      await axios.post("http://localhost:5000/api/family/upload", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      setSelectedFile(null);
      fetchDocuments(); // refresh list
    } catch (err) {
      console.error("Error uploading document:", err);
    }
  };

  useEffect(() => {
    fetchFamilyData();
    fetchDocuments();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (!family) return <p className="text-center mt-10">Loading Family Dashboard...</p>;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-4">Family Dashboard</h1>

      {/* Family Info */}
      <div className="card mb-6">
        <h2 className="text-xl font-semibold mb-2">Family Details</h2>
        <p><strong>Family ID:</strong> {family.familyId}</p>
        <p><strong>Name:</strong> {family.name}</p>
        <p><strong>Address:</strong> {family.address}</p>
      </div>

      {/* Tax Info */}
      <div className="card mb-6">
        <h2 className="text-xl font-semibold mb-2">Tax History</h2>
        {family.taxHistory && family.taxHistory.length > 0 ? (
          <ul className="list-disc ml-6">
            {family.taxHistory.map((tax, idx) => (
              <li key={idx}>
                {tax.month} â†’ â‚¹{tax.amount} ({tax.status})
              </li>
            ))}
          </ul>
        ) : (
          <p>No tax records available.</p>
        )}
      </div>

      {/* Document Upload */}
      <div className="card mb-6">
        <h2 className="text-xl font-semibold mb-2">Upload Document</h2>
        <form onSubmit={handleUpload} className="flex gap-3">
          <input
            type="file"
            onChange={(e) => setSelectedFile(e.target.files[0])}
            className="border p-2"
          />
          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Upload
          </button>
        </form>
      </div>

      {/* Document List */}
      <div className="card">
        <h2 className="text-xl font-semibold mb-2">Uploaded Documents</h2>
        {documents.length > 0 ? (
          documents.map((doc) => <DocumentCard key={doc._id} doc={doc} />)
        ) : (
          <p>No documents uploaded yet.</p>
        )}
      </div>
    </div>
  );
};

export default FamilyDashboard;
