import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
  const token = localStorage.getItem("adminToken");
  const headers = { Authorization: `Bearer ${token}` };

  // Core states
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [historyItems, setHistoryItems] = useState([]);
  const [gallery, setGallery] = useState([]);

  // UI states
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  // Form states
  const [newFamily, setNewFamily] = useState({
    familyId: "", leaderName: "", email: "",
    password: "", members: "", phone: "", address: ""
  });
  const [newEvent, setNewEvent] = useState({ title: "", date: "" });
  const [newWorker, setNewWorker] = useState({ type: "", description: "" });
  const [newHistory, setNewHistory] = useState({ contentEN: "", contentTA: "" });
  const [galleryFile, setGalleryFile] = useState(null);
  const [galleryMeta, setGalleryMeta] = useState({ title: "", description: "" });

  // Tax states
  const [selectedFamilies, setSelectedFamilies] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [taxMonth, setTaxMonth] = useState(() => {
    const d = new Date();
    return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
  });
  const [taxAmount, setTaxAmount] = useState("");
  const [taxPaid, setTaxPaid] = useState(false);

  const [searchFamily, setSearchFamily] = useState("");

  // Load all data
  const loadAll = async () => {
    if (!token) {
      setErrorMsg("Missing admin token. Please login.");
      return;
    }
    try {
      setLoading(true);
      const [fRes, eRes, wRes, hRes, gRes] = await Promise.all([
        axios.get(`${API_BASE}/api/admin/families`, { headers }),
        axios.get(`${API_BASE}/api/admin/events`, { headers }),
        axios.get(`${API_BASE}/api/admin/workers`, { headers }),
        axios.get(`${API_BASE}/api/admin/history`, { headers }),
        axios.get(`${API_BASE}/api/admin/gallery`, { headers }),
      ]);
      setFamilies(fRes.data || []);
      setEvents(eRes.data || []);
      setWorkers(wRes.data || []);
      setHistoryItems(hRes.data || []);
      setGallery(gRes.data || []);
      setErrorMsg("");
    } catch {
      setErrorMsg("Failed to load data. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadAll(); }, []);

  // Family and tax handlers omitted for brevity, please reuse previous implementations

  // Events handlers
  const addEvent = async () => {
    if (!newEvent.title || !newEvent.date) {
      alert("Please provide a title and date for the event.");
      return;
    }
    try {
      setLoading(true);
      await axios.post(`${API_BASE}/api/admin/events`, newEvent, { headers });
      setNewEvent({ title: "", date: "" });
      await loadAll();
    } catch {
      alert("Failed to add event");
    } finally {
      setLoading(false);
    }
  };

  const deleteEvent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    try {
      setLoading(true);
      await axios.delete(`${API_BASE}/api/admin/events/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete event");
    } finally {
      setLoading(false);
    }
  };

  // Workers handlers
  const addWorker = async () => {
    if (!newWorker.type) {
      alert("Please provide worker type");
      return;
    }
    try {
      setLoading(true);
      await axios.post(`${API_BASE}/api/admin/workers`, newWorker, { headers });
      setNewWorker({ type: "", description: "" });
      await loadAll();
    } catch {
      alert("Failed to add worker");
    } finally {
      setLoading(false);
    }
  };

  const deleteWorker = async (id) => {
    if (!window.confirm("Are you sure you want to delete this worker?")) return;
    try {
      setLoading(true);
      await axios.delete(`${API_BASE}/api/admin/workers/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete worker");
    } finally {
      setLoading(false);
    }
  };

  // History handlers
  const addHistory = async () => {
    if (!newHistory.contentEN && !newHistory.contentTA) {
      alert("Please enter content in at least one language.");
      return;
    }
    try {
      setLoading(true);
      await axios.post(`${API_BASE}/api/admin/history`, {
        content: { en: newHistory.contentEN, ta: newHistory.contentTA }
      }, { headers });
      setNewHistory({ contentEN: "", contentTA: "" });
      await loadAll();
    } catch {
      alert("Failed to add history");
    } finally {
      setLoading(false);
    }
  };

  const deleteHistory = async (id) => {
    if (!window.confirm("Are you sure you want to delete this history entry?")) return;
    try {
      setLoading(true);
      await axios.delete(`${API_BASE}/api/admin/history/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete history");
    } finally {
      setLoading(false);
    }
  };

  // Gallery handlers
  const uploadGallery = async () => {
    if (!galleryFile) {
      alert("Please select a file to upload");
      return;
    }
    try {
      setLoading(true);
      const fd = new FormData();
      fd.append("image", galleryFile);
      fd.append("title", galleryMeta.title || "Untitled");
      fd.append("description", galleryMeta.description || "");
      await axios.post(`${API_BASE}/api/admin/gallery/upload`, fd, {
        headers: { ...headers, "Content-Type": "multipart/form-data" }
      });
      setGalleryFile(null);
      setGalleryMeta({ title: "", description: "" });
      await loadAll();
    } catch {
      alert("Failed to upload gallery item");
    } finally {
      setLoading(false);
    }
  };

  const deleteGallery = async (id) => {
    if (!window.confirm("Are you sure you want to delete this gallery item?")) return;
    try {
      setLoading(true);
      await axios.delete(`${API_BASE}/api/admin/gallery/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete gallery item");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-[1200px] mx-auto p-6 space-y-12">

      <h1 className="text-center text-4xl font-bold mb-12">Admin Dashboard</h1>

      {errorMsg && <p className="text-center text-red-600">{errorMsg}</p>}
      {loading && <p className="text-center text-blue-600">Loading...</p>}

      {/* Families and Create Section */}
      {/* (Families and Tax code: reuse as in previous code) */}

      {/* Events Section */}
      <section className="bg-white p-6 rounded shadow space-y-4">
        <h2 className="text-2xl font-semibold">Events</h2>
        <ul className="divide-y divide-gray-200 max-h-64 overflow-auto">
          {events.length === 0 && <li>No events found.</li>}
          {events.map(ev => (
            <li key={ev._id} className="flex justify-between py-2 items-center">
              <span>{ev.title} ({new Date(ev.date).toLocaleDateString()})</span>
              <button className="btn-red btn-xs" onClick={() => deleteEvent(ev._id)}>Delete</button>
            </li>
          ))}
        </ul>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 mt-4">
          <input className="input" placeholder="Title" value={newEvent.title} onChange={e => setNewEvent({ ...newEvent, title: e.target.value })} />
          <input className="input" type="date" value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} />
          <button className="btn-green" onClick={addEvent}>Add</button>
        </div>
      </section>

      {/* Workers Section */}
      <section className="bg-white p-6 rounded shadow space-y-4">
        <h2 className="text-2xl font-semibold">Workers</h2>
        <ul className="divide-y divide-gray-200 max-h-64 overflow-auto">
          {workers.length === 0 && <li>No workers found.</li>}
          {workers.map(w => (
            <li key={w._id} className="flex justify-between py-2 items-center">
              <span>{w.type} â€” {w.description}</span>
              <button className="btn-red btn-xs" onClick={() => deleteWorker(w._id)}>Delete</button>
            </li>
          ))}
        </ul>
        <div className="grid grid-cols-1 gap-2 mt-4">
          <input className="input" placeholder="Type" value={newWorker.type} onChange={e => setNewWorker({ ...newWorker, type: e.target.value })} />
          <input className="input" placeholder="Description" value={newWorker.description} onChange={e => setNewWorker({ ...newWorker, description: e.target.value })} />
          <button className="btn-green" onClick={addWorker}>Add</button>
        </div>
      </section>

      {/* History Section */}
      <section className="bg-white p-6 rounded shadow space-y-4">
        <h2 className="text-2xl font-semibold">History</h2>
        <ul className="divide-y divide-gray-200 max-h-64 overflow-auto">
          {historyItems.length === 0 && <li>No history records.</li>}
          {historyItems.map(h => (
            <li key={h._id} className="flex justify-between py-2 items-center">
              <span>{h.content?.en || h.content?.ta}</span>
              <button className="btn-red btn-xs" onClick={() => deleteHistory(h._id)}>Delete</button>
            </li>
          ))}
        </ul>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 mt-4">
          <input className="input" placeholder="Content English" value={newHistory.contentEN} onChange={e => setNewHistory({ ...newHistory, contentEN: e.target.value })} />
          <input className="input" placeholder="Content Other" value={newHistory.contentTA} onChange={e => setNewHistory({ ...newHistory, contentTA: e.target.value })} />
          <button className="btn-green" onClick={addHistory}>Add</button>
        </div>
      </section>

      {/* Gallery Section */}
      <section className="bg-white p-6 rounded shadow space-y-4">
        <h2 className="text-2xl font-semibold">Gallery</h2>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          {gallery.map(item => (
            <div key={item._id} className="border p-4 rounded flex flex-col">
              <a href={`${API_BASE}${item.url}`} target="_blank" rel="noreferrer" className="truncate mb-2 text-blue-600 hover:underline">{item.title || "Untitled"}</a>
              <p className="text-gray-600 text-sm mb-4 truncate">{item.description || ""}</p>
              <button className="btn-red btn-xs mt-auto" onClick={() => deleteGallery(item._id)}>Delete</button>
            </div>
          ))}
          {gallery.length === 0 && <p>No gallery items.</p>}
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 mt-4">
          <input type="file" onChange={e => setGalleryFile(e.target.files[0])} />
          <input className="input" placeholder="Title" value={galleryMeta.title} onChange={e => setGalleryMeta({ ...galleryMeta, title: e.target.value })} />
          <input className="input" placeholder="Description" value={galleryMeta.description} onChange={e => setGalleryMeta({ ...galleryMeta, description: e.target.value })} />
          <button className="btn-green" onClick={uploadGallery} disabled={!galleryFile}>Upload</button>
        </div>
      </section>
    </div>
  );
};

export default AdminDashboard;
