import React, { useEffect, useState } from "react";

// Reusable label-value component
const InfoItem = ({ label, value }) => (
  <>
    <dt className="font-semibold">{label}:</dt>
    <dd className="text-gray-700">{value || "-"}</dd>
  </>
);

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = process.env.REACT_APP_API_URL || "https://uk-portal-3.com";
  const token = localStorage.getItem("familyToken");

  // Fetch family profile on mount & token changes
  useEffect(() => {
    if (!token) {
      setMsg("⚠️ No authentication token found. Please login.");
      return;
    }
    const fetchProfile = async () => {
      setLoading(true);
      try {
        const res = await fetchFamily();
        setFamily(res);
        setMsg("");
      } catch (error) {
        setMsg("⚠️ Failed to load data.");
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [token]);

  // Fetch family profile
  const fetchFamily = async () => {
    const res = await fetch(`${API_BASE}/api/family/me`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    if (!res.ok) throw new Error("Failed fetching family");
    return res.json();
  };

  // Document upload handler
  const handleUpload = async () => {
    if (!file) {
      setMsg("⚠️ Please select a file");
      return;
    }
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("document", file);
      const res = await fetch(`${API_BASE}/api/family/upload`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData
      });
      if (!res.ok) throw new Error("Upload failed");
      const json = await res.json();
      setFamily(json.family);
      setFile(null);
      setMsg("✅ File uploaded successfully");
    } catch (err) {
      setMsg(err.message || "⚠️ Upload failed");
    } finally {
      setLoading(false);
    }
  };

  // Delete document handler
  const handleDelete = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE}/api/family/document/${docId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Delete failed");
      const json = await res.json();
      setFamily(json.family);
      setMsg("✅ Document deleted successfully");
    } catch (err) {
      setMsg(err.message || "⚠️ Failed to delete document");
    } finally {
      setLoading(false);
    }
  };

  // Toggle tax paid status handler
  const toggleTaxPaidStatus = async (taxId, currentStatus) => {
    if (!taxId) return;
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE}/api/family/tax`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          taxId,
          paid: !currentStatus,
        }),
      });
      if (!res.ok) throw new Error("Toggle failed");
      const json = await res.json();
      const updatedProfile = await fetchFamily();
      setFamily(updatedProfile);
      setMsg(`✅ Tax marked as ${!currentStatus ? "Paid" : "Pending"}`);
    } catch (err) {
      setMsg(err.message || "⚠️ Failed to update tax status");
    } finally {
      setLoading(false);
    }
  };

  // Delete tax record handler
  const handleDeleteTax = async (taxId) => {
    if (!window.confirm("Are you sure you want to delete this tax record?")) return;
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE}/api/family/tax/${taxId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Delete failed");
      const json = await res.json();
      setFamily(json.family);
      setMsg("✅ Tax record deleted successfully");
    } catch (err) {
      setMsg(err.message || "⚠️ Failed to delete tax record");
    } finally {
      setLoading(false);
    }
  };

  if (!family) {
    return (
      <div className="p-8 text-center animate-pulse text-gray-600 text-lg font-semibold">
        ⏳ Loading family data...
      </div>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-10 font-sans">
      <h1 className="text-3xl font-bold text-center text-green-700 mb-8">🏠 Family Dashboard</h1>
      {loading && <p className="text-blue-600 font-semibold text-center">Processing...</p>}
      {msg && <p className="text-yellow-700 font-medium text-center">{msg}</p>}

      {/* Family Information */}
      <section className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold border-b border-gray-300 pb-2 mb-6 text-gray-800">Family Information</h2>
        <dl className="grid grid-cols-1 sm:grid-cols-2 gap-x-6 gap-y-4 text-gray-700 text-sm">
          <InfoItem label="Family ID" value={family.familyId} />
          <InfoItem label="Leader" value={family.leaderName} />
          <InfoItem label="Members" value={family.members?.join(", ")} />
          <InfoItem label="Address" value={family.address} />
          <InfoItem label="Email" value={family.email} />
          <InfoItem label="Phone" value={family.phone} />
        </dl>
      </section>

      {/* Tax Information */}
      <section className="bg-white rounded-lg shadow p-6 overflow-auto max-h-80">
        <h2 className="text-xl font-semibold border-b border-gray-300 pb-2 mb-6 text-gray-800">Taxes</h2>
        {family.taxHistory?.length ? (
          <table className="min-w-full border-collapse rounded-lg border border-gray-300 overflow-hidden">
            <thead className="bg-gray-100 sticky top-0">
              <tr>
                <th className="p-3 border text-left">Month</th>
                <th className="p-3 border text-right">Amount (₹)</th>
                <th className="p-3 border text-center">Status</th>
                <th className="p-3 border text-center">Action</th>
                <th className="p-3 border text-center">Delete</th>
              </tr>
            </thead>
            <tbody>
              {family.taxHistory.map((tax) => (
                <tr key={tax._id} className="hover:bg-gray-50 transition-colors">
                  <td className="p-3 border">{tax.month}</td>
                  <td className="p-3 border text-right">{tax.amount}</td>
                  <td className={`p-3 border text-center font-bold ${tax.paid ? "text-green-600" : "text-red-600"}`}>
                    {tax.paid ? "Paid" : "Pending"}
                  </td>
                  <td className="p-3 border text-center">
                    <button
                      onClick={() => toggleTaxPaidStatus(tax._id, tax.paid)}
                      className={`px-4 py-1 rounded focus:outline-none transition ${
                        tax.paid ? "bg-yellow-500 hover:bg-yellow-600" : "bg-green-600 hover:bg-green-700 text-white"
                      }`}
                      disabled={loading}
                      aria-label={`Mark tax for ${tax.month} as ${tax.paid ? "Pending" : "Paid"}`}
                    >
                      Mark as {tax.paid ? "Pending" : "Paid"}
                    </button>
                  </td>
                  <td className="p-3 border text-center">
                    <button
                      onClick={() => handleDeleteTax(tax._id)}
                      className="bg-red-600 hover:bg-red-700 text-white px-4 py-1 rounded"
                      disabled={loading}
                      aria-label={`Delete tax record for ${tax.month}`}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-600">No tax records found.</p>
        )}
      </section>

      {/* Document upload */}
      <section className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold border-b border-gray-300 pb-2 mb-6 text-gray-800">Upload Document</h2>
        <div className="flex flex-col sm:flex-row gap-4 items-center">
          <input
            type="file"
            onChange={e => setFile(e.target.files[0])}
            className="border border-gray-300 rounded px-3 py-2 w-full sm:w-auto"
            aria-label="Select document file"
          />
          <button
            onClick={handleUpload}
            disabled={loading || !file}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded focus:outline-none"
          >
            Upload
          </button>
        </div>
      </section>

      {/* Uploaded documents */}
      <section className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold border-b border-gray-300 pb-2 mb-6 text-gray-800">Uploaded Documents</h2>
        {family.documents?.length ? (
          <ul className="space-y-3">
            {family.documents.map(doc => (
              <li
                key={doc._id}
                className="flex flex-col sm:flex-row justify-between items-center gap-2 sm:gap-4 bg-gray-50 rounded px-3 py-2"
              >
                <span className="truncate max-w-xs">{doc.originalName || doc.filename}</span>
                <span className="text-gray-500 text-xs">{new Date(doc.uploadedAt).toLocaleString()}</span>
                <div className="flex gap-4">
                  <a
                    href={`${API_BASE}${doc.path}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    download
                    className="text-blue-600 hover:underline"
                    aria-label={`Download ${doc.originalName || doc.filename}`}
                  >
                    Download
                  </a>
                  <button
                    onClick={() => handleDelete(doc._id)}
                    disabled={loading}
                    className="text-red-600 hover:underline focus:outline-none"
                    aria-label={`Delete ${doc.originalName || doc.filename}`}
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No documents uploaded.</p>
        )}
      </section>
    </div>
  );
};

export default FamilyDashboard;
