import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("familyToken"); // stored after login

  // Fetch family details
  const fetchFamily = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/family/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamily(res.data);
      setDocuments(res.data.documents || []);
    } catch (err) {
      console.error("Error fetching family data:", err);
    }
  };

  useEffect(() => {
    fetchFamily();
  }, []);

  // Upload document
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return setMessage("Please select a file");
    const formData = new FormData();
    formData.append("document", file);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/family/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setDocuments(res.data.documents);
      setMessage("File uploaded successfully");
      setFile(null);
    } catch (err) {
      console.error("Error uploading file:", err);
      setMessage("Upload failed");
    }
  };

  if (!family) return <p className="p-6 text-gray-600">Loading...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h1 className="text-3xl font-bold mb-4">Family Dashboard</h1>

      <div className="border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Family Details</h2>
        <table className="w-full border-collapse border text-left">
          <tbody>
            <tr><td className="p-2 font-bold">Family ID:</td><td>{family.familyId}</td></tr>
            <tr><td className="p-2 font-bold">Leader Name:</td><td>{family.leaderName}</td></tr>
            <tr><td className="p-2 font-bold">Members:</td><td>{family.members.join(", ")}</td></tr>
            <tr><td className="p-2 font-bold">Address:</td><td>{family.address}</td></tr>
            <tr><td className="p-2 font-bold">Phone:</td><td>{family.phone}</td></tr>
            <tr><td className="p-2 font-bold">Email:</td><td>{family.email}</td></tr>
          </tbody>
        </table>
      </div>

      <div className="border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Upload Documents</h2>
        <form onSubmit={handleUpload} className="space-y-2">
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <button className="bg-green-600 text-white px-4 py-2 rounded" type="submit">
            Upload
          </button>
        </form>
        {message && <p className="text-green-700 mt-2">{message}</p>}

        {documents.length > 0 && (
          <div className="mt-4">
            <h3 className="font-semibold mb-2">Uploaded Documents:</h3>
            <ul className="list-disc pl-6">
              {documents.map((doc, idx) => (
                <li key={idx}>
                  <a
                    href={`http://localhost:5000/${doc.path}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                  >
                    {doc.filename}
                  </a>
                  <span className="ml-2 text-gray-500">
                    ({new Date(doc.uploadedAt).toLocaleDateString()})
                  </span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <div className="border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Tax History</h2>
        {family.taxHistory && family.taxHistory.length > 0 ? (
          <table className="w-full border-collapse border text-left">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-2 border">Month</th>
                <th className="p-2 border">Amount</th>
                <th className="p-2 border">Paid</th>
              </tr>
            </thead>
            <tbody>
              {family.taxHistory.map((t, idx) => (
                <tr key={idx}>
                  <td className="p-2 border">{t.month}</td>
                  <td className="p-2 border">{t.amount}</td>
                  <td className="p-2 border">{t.paid ? "Yes" : "No"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : <p>No tax records yet.</p>}
      </div>
    </div>
  );
};

export default FamilyDashboard;
