import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [history, setHistory] = useState([]);

  // Form states
  const [newEvent, setNewEvent] = useState({ title: "", description: "", date: "" });
  const [newAchievement, setNewAchievement] = useState({ title: "", description: "" });
  const [newHistory, setNewHistory] = useState({ title: "", description: "" });

  const token = localStorage.getItem("adminToken");

  // ------------------- Fetch Data -------------------
  const fetchFamilies = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/families", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamilies(res.data);
    } catch (err) {
      console.error("Error fetching families:", err.response?.data || err.message);
    }
  };

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/events", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(res.data);
    } catch {
      setEvents([]);
    }
  };

  const fetchAchievements = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/achievements", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAchievements(res.data);
    } catch {
      setAchievements([]);
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistory(res.data);
    } catch {
      setHistory([]);
    }
  };

  useEffect(() => {
    if (token) {
      fetchFamilies();
      fetchEvents();
      fetchAchievements();
      fetchHistory();
    }
  }, [token]);

  // ------------------- Family Approve/Reject -------------------
  const handleApprove = async (familyId, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchFamilies();
    } catch (err) {
      console.error("Error updating approval:", err.response?.data || err.message);
    }
  };

  // ------------------- Events CRUD -------------------
  const handleAddEvent = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/events",
        newEvent,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setEvents([...events, res.data]);
      setNewEvent({ title: "", description: "", date: "" });
    } catch (err) {
      console.error("Error adding event:", err.response?.data || err.message);
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/events/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(events.filter((e) => e._id !== id));
    } catch (err) {
      console.error("Error deleting event:", err.response?.data || err.message);
    }
  };

  // ------------------- Achievements CRUD -------------------
  const handleAddAchievement = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/achievements",
        newAchievement,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAchievements([...achievements, res.data]);
      setNewAchievement({ title: "", description: "" });
    } catch (err) {
      console.error("Error adding achievement:", err.response?.data || err.message);
    }
  };

  const handleDeleteAchievement = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/achievements/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAchievements(achievements.filter((a) => a._id !== id));
    } catch (err) {
      console.error("Error deleting achievement:", err.response?.data || err.message);
    }
  };

  // ------------------- History CRUD -------------------
  const handleAddHistory = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/history",
        newHistory,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHistory([...history, res.data]);
      setNewHistory({ title: "", description: "" });
    } catch (err) {
      console.error("Error adding history:", err.response?.data || err.message);
    }
  };

  const handleDeleteHistory = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/history/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistory(history.filter((h) => h._id !== id));
    } catch (err) {
      console.error("Error deleting history:", err.response?.data || err.message);
    }
  };

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-8">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Families Table */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Families</h2>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-green-700 text-white">
              <th className="border p-2">Family ID</th>
              <th className="border p-2">Leader</th>
              <th className="border p-2">Tax</th>
              <th className="border p-2">Docs</th>
              <th className="border p-2">Approved</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.map((f) => (
              <tr key={f._id}>
                <td className="border p-2">{f.familyId}</td>
                <td className="border p-2">{f.leaderName}</td>
                <td className="border p-2">
                  {f.taxHistory.length > 0
                    ? f.taxHistory[f.taxHistory.length - 1].amount
                    : 0}{" "}
                  INR
                </td>
                <td className="border p-2">
                  {f.documents && f.documents.length > 0
                    ? f.documents.map((doc, i) => (
                        <div key={i}>
                          <a
                            href={`http://localhost:5000/${doc.path}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-blue-600 underline"
                          >
                            {doc.filename}
                          </a>
                        </div>
                      ))
                    : "No Docs"}
                </td>
                <td className="border p-2">{f.approved ? "Yes" : "No"}</td>
                <td className="border p-2">
                  {!f.approved && (
                    <button
                      className="bg-green-600 text-white px-2 py-1 rounded"
                      onClick={() => handleApprove(f._id, true)}
                    >
                      Approve
                    </button>
                  )}
                  {f.approved && (
                    <button
                      className="bg-red-600 text-white px-2 py-1 rounded"
                      onClick={() => handleApprove(f._id, false)}
                    >
                      Reject
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Events Section */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Events</h2>
        <div className="mb-4 flex space-x-2">
          <input
            type="text"
            placeholder="Title"
            value={newEvent.title}
            onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
            className="border p-2"
          />
          <input
            type="text"
            placeholder="Description"
            value={newEvent.description}
            onChange={(e) =>
              setNewEvent({ ...newEvent, description: e.target.value })
            }
            className="border p-2"
          />
          <input
            type="date"
            value={newEvent.date}
            onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
            className="border p-2"
          />
          <button
            onClick={handleAddEvent}
            className="bg-blue-600 text-white px-3 rounded"
          >
            Add
          </button>
        </div>
        {events.map((e) => (
          <div key={e._id} className="border p-2 flex justify-between">
            <div>
              <strong>{e.title}</strong> - {e.description} (
              {new Date(e.date).toLocaleDateString()})
            </div>
            <button
              onClick={() => handleDeleteEvent(e._id)}
              className="bg-red-600 text-white px-2 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      {/* Achievements Section */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Achievements</h2>
        <div className="mb-4 flex space-x-2">
          <input
            type="text"
            placeholder="Title"
            value={newAchievement.title}
            onChange={(e) =>
              setNewAchievement({ ...newAchievement, title: e.target.value })
            }
            className="border p-2"
          />
          <input
            type="text"
            placeholder="Description"
            value={newAchievement.description}
            onChange={(e) =>
              setNewAchievement({
                ...newAchievement,
                description: e.target.value,
              })
            }
            className="border p-2"
          />
          <button
            onClick={handleAddAchievement}
            className="bg-blue-600 text-white px-3 rounded"
          >
            Add
          </button>
        </div>
        {achievements.map((a) => (
          <div key={a._id} className="border p-2 flex justify-between">
            <div>
              <strong>{a.title}</strong> - {a.description}
            </div>
            <button
              onClick={() => handleDeleteAchievement(a._id)}
              className="bg-red-600 text-white px-2 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      {/* History Section */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">History</h2>
        <div className="mb-4 flex space-x-2">
          <input
            type="text"
            placeholder="Title"
            value={newHistory.title}
            onChange={(e) => setNewHistory({ ...newHistory, title: e.target.value })}
            className="border p-2"
          />
          <input
            type="text"
            placeholder="Description"
            value={newHistory.description}
            onChange={(e) =>
              setNewHistory({ ...newHistory, description: e.target.value })
            }
            className="border p-2"
          />
          <button
            onClick={handleAddHistory}
            className="bg-blue-600 text-white px-3 rounded"
          >
            Add
          </button>
        </div>
        {history.map((h) => (
          <div key={h._id} className="border p-2 flex justify-between">
            <div>
              <strong>{h.title}</strong> - {h.description}
            </div>
            <button
              onClick={() => handleDeleteHistory(h._id)}
              className="bg-red-600 text-white px-2 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
