import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [file, setFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [taxNotification, setTaxNotification] = useState("");
  const token = localStorage.getItem("familyToken"); // stored on login

  // Fetch family details
  useEffect(() => {
    const fetchFamily = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/family/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const familyData = res.data;
        setFamily(familyData);

        // Check unpaid tax
        const latestTax = familyData.taxHistory.length
          ? familyData.taxHistory[familyData.taxHistory.length - 1]
          : null;
        if (latestTax && !latestTax.paid) {
          setTaxNotification(
            `‚ö†Ô∏è You have unpaid tax for ${latestTax.month}: ‚Çπ${latestTax.amount}`
          );
        }
      } catch (err) {
        console.error(err);
      }
    };
    fetchFamily();
  }, [token]);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append("document", file);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/family/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setFamily(res.data.family || family); // update documents
      setUploadMessage("File uploaded successfully");
    } catch (err) {
      console.error(err);
      setUploadMessage("Upload failed");
    }
  };

  if (!family) return <p className="p-6">Loading...</p>;

  // Latest tax entry
  const latestTax =
    family.taxHistory.length > 0
      ? family.taxHistory[family.taxHistory.length - 1]
      : null;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">
        üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Family Dashboard
      </h2>

      {/* Family Info */}
      <table className="w-full border border-gray-300 rounded mb-6">
        <tbody>
          <tr>
            <td className="p-2 font-semibold">Family ID</td>
            <td className="p-2">{family.familyId}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Leader Name</td>
            <td className="p-2">{family.leaderName}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Members</td>
            <td className="p-2">{family.members.join(", ")}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Address</td>
            <td className="p-2">{family.address}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Phone</td>
            <td className="p-2">{family.phone}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Email</td>
            <td className="p-2">{family.email}</td>
          </tr>
          <tr>
            <td className="p-2 font-semibold">Current Tax</td>
            <td className="p-2">
              {latestTax
                ? `${latestTax.amount} INR (${latestTax.paid ? "Paid" : "Pending"})`
                : "0 INR"}
            </td>
          </tr>
        </tbody>
      </table>

      {/* Tax Notification */}
      {taxNotification && (
        <p className="mb-4 p-2 bg-yellow-100 text-yellow-800 rounded border border-yellow-300">
          {taxNotification}
        </p>
      )}

      {/* Document Upload */}
      <div className="mb-6">
        <h3 className="font-semibold mb-2">Upload Documents</h3>
        <input type="file" onChange={handleFileChange} />
        <button
          onClick={handleUpload}
          className="ml-2 bg-blue-600 text-white px-4 py-1 rounded"
        >
          Upload
        </button>
        {uploadMessage && (
          <p className="mt-2 text-green-700">{uploadMessage}</p>
        )}
      </div>

      {/* Uploaded Documents */}
      <div>
        <h3 className="font-semibold mb-2">Uploaded Documents</h3>
        <ul className="list-disc pl-5">
          {family.documents && family.documents.length > 0 ? (
            family.documents.map((doc, index) => (
              <li key={index}>
                <a
                  href={`http://localhost:5000/${doc.path}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 underline"
                >
                  {doc.filename}
                </a>{" "}
                - {new Date(doc.uploadedAt).toLocaleDateString()}
              </li>
            ))
          ) : (
            <li>No documents uploaded yet.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default FamilyDashboard;
