import React, { useEffect, useState } from "react";
import axios from "axios";
import GalleryModal from "../components/GalleryModal";

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGallery = async () => {
      try {
        // ✅ Correct API endpoint (update backend if needed)
        const res = await axios.get("http://localhost:5000/api/admin/gallery");
        setImages(res.data);
      } catch (err) {
        console.error("Error fetching gallery:", err);
        setError("Failed to load gallery. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchGallery();
  }, []);

  if (loading) {
    return (
      <div className="p-8 text-center text-lg font-semibold">
        Loading gallery...
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 text-center text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">Photo Gallery</h2>

      {images.length === 0 ? (
        <p className="text-gray-600 text-lg text-center">
          No images available in the gallery.
        </p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {images.map((img, idx) => (
            <img
              key={idx}
              src={img.url}
              alt={img.title || `Gallery ${idx}`}
              className="cursor-pointer rounded shadow hover:scale-105 transition"
              onClick={() => setSelectedImage(img.url)}
            />
          ))}
        </div>
      )}

      {/* Modal for preview */}
      {selectedImage && (
        <GalleryModal image={selectedImage} onClose={() => setSelectedImage(null)} />
      )}
    </div>
  );
};

export default Gallery;
