import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchFamilies = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/families");
        setFamilies(res.data);
      } catch (err) {
        console.error("Error fetching families:", err);
        setError("Failed to load family data.");
      } finally {
        setLoading(false);
      }
    };

    fetchFamilies();
  }, []);

  if (loading) return <p className="p-6 text-gray-600">Loading families...</p>;
  if (error) return <p className="p-6 text-red-500">{error}</p>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4 text-center">üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Families</h2>

      <div className="overflow-x-auto">
        <table className="w-full border border-gray-300 rounded-lg shadow-md">
          <thead>
            <tr className="bg-green-600 text-white">
              <th className="p-3 text-left">Family ID</th>
              <th className="p-3 text-left">Leader</th>
              <th className="p-3 text-left">Members</th>
              <th className="p-3 text-left">Address</th>
              <th className="p-3 text-left">Phone</th>
              <th className="p-3 text-left">Email</th>
            </tr>
          </thead>
          <tbody>
            {families.length > 0 ? (
              families.map((family) => (
                <tr key={family._id} className="border-t hover:bg-gray-100">
                  <td className="p-3">{family.familyId}</td>
                  <td className="p-3">{family.leaderName}</td>
                  <td className="p-3">
                    {Array.isArray(family.members) && family.members.length > 0
                      ? family.members.join(", ")
                      : "N/A"}
                  </td>
                  <td className="p-3">{family.address || "N/A"}</td>
                  <td className="p-3">{family.phone || "N/A"}</td>
                  <td className="p-3">{family.email || "N/A"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="p-4 text-center text-gray-500">
                  No families found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FamilyDashboard;
