import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [lang, setLang] = useState("en");
  const [isOpen, setIsOpen] = useState(false);

  const toggleLanguage = () => {
    const newLang = lang === "en" ? "ta" : "en";
    i18n.changeLanguage(newLang);
    setLang(newLang);
  };

  return (
    <nav className="bg-green-700 text-white p-4">
      <div className="flex justify-between items-center">
        {/* Logo or Title */}
        <div className="text-xl font-bold">üåø {t("home")}</div>

        {/* Desktop Menu */}
        <div className="hidden md:flex space-x-6">
          <Link to="/" className="hover:text-yellow-300">{t("home")}</Link>
          <Link to="/history" className="hover:text-yellow-300">{t("history")}</Link>
          <Link to="/events" className="hover:text-yellow-300">{t("events")}</Link>
          <Link to="/gallery" className="hover:text-yellow-300">{t("gallery")}</Link>
          <Link to="/workers" className="hover:text-yellow-300">{t("workers")}</Link>
          <Link to="/family-login" className="hover:text-yellow-300">{t("familyLogin")}</Link>
          <Link to="/admin-login" className="hover:text-yellow-300">{t("adminLogin")}</Link>
          <button
            onClick={toggleLanguage}
            className="bg-yellow-400 text-black px-3 py-1 rounded"
          >
            üåê {lang === "en" ? "TA" : "EN"}
          </button>
        </div>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white text-2xl"
          onClick={() => setIsOpen(!isOpen)}
        >
          {isOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* Mobile Dropdown */}
      {isOpen && (
        <div className="md:hidden mt-4 flex flex-col space-y-3">
          <Link to="/" className="hover:text-yellow-300">{t("home")}</Link>
          <Link to="/history" className="hover:text-yellow-300">{t("history")}</Link>
          <Link to="/events" className="hover:text-yellow-300">{t("events")}</Link>
          <Link to="/gallery" className="hover:text-yellow-300">{t("gallery")}</Link>
          <Link to="/workers" className="hover:text-yellow-300">{t("workers")}</Link>
          <Link to="/family-login" className="hover:text-yellow-300">{t("familyLogin")}</Link>
          <Link to="/admin-login" className="hover:text-yellow-300">{t("adminLogin")}</Link>
          <button
            onClick={toggleLanguage}
            className="bg-yellow-400 text-black px-3 py-1 rounded"
          >
            üåê {lang === "en" ? "TA" : "EN"}
          </button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
