import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("ADMIN_TOKEN");

  // -------------------
  // Fetch Families
  // -------------------
  const fetchFamilies = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/families", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFamilies(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // -------------------
  // Fetch Events
  // -------------------
  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/events", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFamilies();
    fetchEvents();
  }, [token]);

  // -------------------
  // Approve / Reject Family
  // -------------------
  const handleApprove = async (familyId, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId, approved },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFamilies((prev) =>
        prev.map((f) => (f.familyId === familyId ? { ...f, approved } : f))
      );
    } catch (err) {
      console.error(err);
    }
  };

  // -------------------
  // Event Handlers
  // -------------------
  const handleDeleteEvent = async (id) => {
    try {
      await axios.delete("http://localhost:5000/api/admin/events/" + id, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEvents((prev) => prev.filter((e) => e._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleSendReminders = async () => {
    setLoading(true);
    setMessage("");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/send-tax-reminders",
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message);
    } catch (err) {
      console.error(err);
      setMessage("Error sending reminders");
    }
    setLoading(false);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-8">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Tax Notifications */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Tax Notifications</h2>
        <button
          onClick={handleSendReminders}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={loading}
        >
          {loading ? "Sending..." : "Send Tax Reminders (Email/SMS)"}
        </button>
        {message && <p className="mt-2 text-green-700">{message}</p>}
      </div>

      {/* Families Table */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Families</h2>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-green-700 text-white">
              <th className="border p-2">Family ID</th>
              <th className="border p-2">Name</th>
              <th className="border p-2">Address</th>
              <th className="border p-2">Approved</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.map((f) => (
              <tr key={f.familyId}>
                <td className="border p-2">{f.familyId}</td>
                <td className="border p-2">{f.name}</td>
                <td className="border p-2">{f.address}</td>
                <td className="border p-2">{f.approved ? "Yes" : "No"}</td>
                <td className="border p-2 space-x-2">
                  {!f.approved && (
                    <button
                      onClick={() => handleApprove(f.familyId, true)}
                      className="bg-green-600 text-white px-2 py-1 rounded"
                    >
                      Approve
                    </button>
                  )}
                  {f.approved && (
                    <button
                      onClick={() => handleApprove(f.familyId, false)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Reject
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Events Table */}
      <div className="card p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Events</h2>
        <table className="w-full border-collapse border text-center">
          <thead>
            <tr className="bg-purple-700 text-white">
              <th className="border p-2">Title</th>
              <th className="border p-2">Description</th>
              <th className="border p-2">Date</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map((e) => (
              <tr key={e._id}>
                <td className="border p-2">{e.title}</td>
                <td className="border p-2">{e.description}</td>
                <td className="border p-2">{new Date(e.date).toLocaleDateString()}</td>
                <td className="border p-2 space-x-2">
                  <button
                    onClick={() => handleDeleteEvent(e._id)}
                    className="bg-red-600 text-white px-2 py-1 rounded"
                  >
                    Delete
                  </button>
                  {/* You can add update/edit button here */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
