import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
  const token = localStorage.getItem("adminToken");
  const headers = { Authorization: `Bearer ${token}` };

  // Core states
  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [historyItems, setHistoryItems] = useState([]);
  const [gallery, setGallery] = useState([]);

  // UI states
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  // Form states
  const [newFamily, setNewFamily] = useState({
    familyId: "",
    leaderName: "",
    email: "",
    password: "",
    members: "",
    phone: "",
    address: ""
  });

  const [newEvent, setNewEvent] = useState({ title: "", date: "" });
  const [newWorker, setNewWorker] = useState({ type: "", description: "" });
  const [newHistory, setNewHistory] = useState({ contentEN: "", contentTA: "" });
  const [galleryFile, setGalleryFile] = useState(null);
  const [galleryMeta, setGalleryMeta] = useState({ title: "", description: "" });

  // Tax states
  const [selectedFamilies, setSelectedFamilies] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [taxMonth, setTaxMonth] = useState(() => {
    const d = new Date();
    return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
  });
  const [taxAmount, setTaxAmount] = useState("");
  const [taxPaid, setTaxPaid] = useState(false);

  const [searchFamily, setSearchFamily] = useState("");

  // Load all dashboard data
  const loadAll = async () => {
    if (!token) {
      setErrorMsg("Please log in to access the dashboard.");
      return;
    }
    setLoading(true);
    setErrorMsg("");
    try {
      const [famRes, evtRes, workRes, histRes, galRes] = await Promise.all([
        axios.get(`${API_BASE}/admin/families`, { headers }),
        axios.get(`${API_BASE}/admin/events`, { headers }),
        axios.get(`${API_BASE}/admin/workers`, { headers }),
        axios.get(`${API_BASE}/admin/history`, { headers }),
        axios.get(`${API_BASE}/admin/gallery`, { headers }),
      ]);
      setFamilies(famRes.data || []);
      setEvents(evtRes.data || []);
      setWorkers(workRes.data || []);
      setHistoryItems(histRes.data || []);
      setGallery(galRes.data || []);
    } catch (error) {
      setErrorMsg("Failed to load data.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAll();
  }, [token]);

  // Families CRUD
  const createFamily = async () => {
    if (!newFamily.familyId || !newFamily.leaderName || !newFamily.email || !newFamily.password) {
      alert("Please fill all required fields.");
      return;
    }
    setLoading(true);
    try {
      await axios.post(
        `${API_BASE}/admin/families`,
        {
          ...newFamily,
          members: newFamily.members ? newFamily.members.split(",").map(m => m.trim()) : []
        },
        { headers }
      );
      setNewFamily({ familyId: "", leaderName: "", email: "", password: "", members: "", phone: "", address: "" });
      await loadAll();
    } catch {
      alert("Failed to create family.");
    } finally {
      setLoading(false);
    }
  };

  const approveFamily = async (id, approve) => {
    if (!window.confirm(`Confirm to ${approve ? "approve" : "reject"} this family?`)) return;
    setLoading(true);
    try {
      await axios.post(`${API_BASE}/admin/families/${approve ? "approve" : "reject"}`, { id }, { headers });
      await loadAll();
    } catch {
      alert("Failed to update family status.");
    } finally {
      setLoading(false);
    }
  };

  // Tax
  const toggleFamilySelection = (id) => {
    setSelectedFamilies(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]);
  };

  const toggleSelectAll = () => {
    if (selectAll) {
      setSelectedFamilies([]);
      setSelectAll(false);
    } else {
      setSelectedFamilies(families.map(f => f._id));
      setSelectAll(true);
    }
  };

  const addTax = async () => {
    if (!taxMonth || !taxAmount || selectedFamilies.length === 0) {
      alert("Fill all tax details and select families.");
      return;
    }
    setLoading(true);
    try {
      await axios.post(
        `${API_BASE}/admin/families/tax`,
        {
          familyIds: selectedFamilies,
          month: taxMonth,
          amount: taxAmount,
          paid: taxPaid,
        },
        { headers }
      );
      setSelectedFamilies([]);
      setSelectAll(false);
      setTaxAmount("");
      setTaxPaid(false);
      await loadAll();
    } catch {
      alert("Failed to add tax.");
    } finally {
      setLoading(false);
    }
  };

  const markTaxPaid = async (familyId, month) => {
    setLoading(true);
    try {
      await axios.post(
        `${API_BASE}/admin/families/tax`,
        {
          familyIds: [familyId],
          month,
          paid: true,
        },
        { headers }
      );
      await loadAll();
    } catch {
      alert("Failed to update tax status.");
    } finally {
      setLoading(false);
    }
  };

  // Events
  const addEvent = async () => {
    if (!newEvent.title || !newEvent.date) {
      alert("Fill all event fields.");
      return;
    }
    setLoading(true);
    try {
      await axios.post(`${API_BASE}/admin/events`, newEvent, { headers });
      setNewEvent({ title: "", date: "" });
      await loadAll();
    } catch {
      alert("Failed to add event.");
    } finally {
      setLoading(false);
    }
  };

  const deleteEvent = async (id) => {
    if (!window.confirm("Confirm to delete event?")) return;
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/admin/events/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete event.");
    } finally {
      setLoading(false);
    }
  };

  // Workers
  const addWorker = async () => {
    if (!newWorker.type) {
      alert("Worker type is required.");
      return;
    }
    setLoading(true);
    try {
      await axios.post(`${API_BASE}/admin/workers`, newWorker, { headers });
      setNewWorker({ type: "", description: "" });
      await loadAll();
    } catch {
      alert("Failed to add worker.");
    } finally {
      setLoading(false);
    }
  };

  const deleteWorker = async (id) => {
    if (!window.confirm("Confirm to delete worker?")) return;
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/admin/workers/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete worker.");
    } finally {
      setLoading(false);
    }
  };

  // History
  const addHistory = async () => {
    if (!newHistory.contentEN && !newHistory.contentTA) {
      alert("Fill at least one language content.");
      return;
    }
    setLoading(true);
    try {
      await axios.post(
        `${API_BASE}/admin/history`,
        { content: { en: newHistory.contentEN, ta: newHistory.contentTA } },
        { headers }
      );
      setNewHistory({ contentEN: "", contentTA: "" });
      await loadAll();
    } catch {
      alert("Failed to add history.");
    } finally {
      setLoading(false);
    }
  };

  const deleteHistory = async (id) => {
    if (!window.confirm("Confirm to delete history?")) return;
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/admin/history/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete history.");
    } finally {
      setLoading(false);
    }
  };

  // Gallery
  const uploadGallery = async () => {
    if (!galleryFile) {
      alert("Select a file first.");
      return;
    }
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("image", galleryFile);
      formData.append("title", galleryMeta.title || "Untitled");
      formData.append("description", galleryMeta.description || "");
      await axios.post(`${API_BASE}/admin/gallery/upload`, formData, {
        headers: { ...headers, "Content-Type": "multipart/form-data" }
      });
      setGalleryFile(null);
      setGalleryMeta({ title: "", description: "" });
      await loadAll();
    } catch {
      alert("Failed to upload gallery item.");
    } finally {
      setLoading(false);
    }
  };

  const deleteGallery = async (id) => {
    if (!window.confirm("Confirm to delete gallery item?")) return;
    setLoading(true);
    try {
      await axios.delete(`${API_BASE}/admin/gallery/${id}`, { headers });
      await loadAll();
    } catch {
      alert("Failed to delete gallery.");
    } finally {
      setLoading(false);
    }
  };

  // Filtering families
  const filteredFamilies = useMemo(() => {
    if (!searchFamily.trim()) return families;
    const lower = searchFamily.toLowerCase();
    return families.filter(f =>
      (f.familyId + " " + f.leaderName + " " + (f.email ?? "")).toLowerCase().includes(lower)
    );
  }, [families, searchFamily]);

  // Filtering tax statuses
  const paidFamilies = useMemo(() =>
    families.filter(f => f.taxHistory?.some(t => t.month === taxMonth && t.paid)), [families, taxMonth]);

  const unpaidFamilies = useMemo(() =>
    families.filter(f => !f.taxHistory?.some(t => t.month === taxMonth && t.paid)), [families, taxMonth]);

  const totalPaid = useMemo(() =>
    paidFamilies.reduce((sum, f) => sum + (Number(f.taxHistory.find(t => t.month === taxMonth && t.paid)?.amount) || 0), 0),
    [paidFamilies, taxMonth]);

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-16 font-sans">

      <h1 className="text-center text-4xl font-bold mb-10">Admin Dashboard</h1>

      {errorMsg && <p className="text-center text-red-600">{errorMsg}</p>}
      {loading && <p className="text-center text-blue-600">Loading...</p>}

      {/* Families & Add Family Section */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Families List */}
        <section className="md:col-span-2 flex flex-col">
          {/* Search and Actions */}
          <div className="flex flex-wrap justify-between items-center mb-6">
            <input
              type="search"
              placeholder="Search families"
              value={searchFamily}
              onChange={e => setSearchFamily(e.target.value)}
              className="p-2 border rounded w-full sm:w-64"
            />
            <div className="flex space-x-3 mt-2 sm:mt-0">
              <button className="btn-secondary" onClick={loadAll}>Refresh</button>
              <button className="btn-yellow" onClick={() => { if(window.confirm("Send notifications to all families?")) alert("Notifications sent") }}>Notify All</button>
            </div>
          </div>
          <div className="overflow-auto rounded border max-h-96">
            <table className="w-full border-collapse text-sm">
              <thead className="sticky top-0 bg-gray-100 border-b font-semibold">
                <tr>
                  <th className="w-12 text-center">
                    <input
                      type="checkbox"
                      checked={selectAll}
                      onChange={() => {
                        if (selectAll) setSelectedFamilies([]); else setSelectedFamilies(families.map(f => f._id));
                        setSelectAll(!selectAll);
                      }}
                    />
                  </th>
                  <th>ID</th>
                  <th>Leader</th>
                  <th className="text-center">Approved</th>
                  <th>Latest Tax</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {!filteredFamilies.length && (
                  <tr><td colSpan={6} className="text-center p-4">No families found.</td></tr>
                )}
                {filteredFamilies.map(f => {
                  const lastTax = f.taxHistory?.slice(-1)[0];
                  return (
                    <tr key={f._id} className="hover:bg-gray-50">
                      <td className="text-center">
                        <input type="checkbox" checked={selectedFamilies.includes(f._id)} onChange={() => toggleFamilySelection(f._id)} />
                      </td>
                      <td>{f.familyId}</td>
                      <td>{f.leaderName}</td>
                      <td className="text-center">{f.approved ? "Yes" : "No"}</td>
                      <td>{lastTax ? `${lastTax.month} ₹${lastTax.amount} (${lastTax.paid ? "Paid" : "Pending"})` : "-"}</td>
                      <td className="text-center space-x-2">
                        <button className="btn-green btn-xs" onClick={() => approveFamily(f._id, true)} disabled={loading}>Approve</button>
                        <button className="btn-red btn-xs" onClick={() => approveFamily(f._id, false)} disabled={loading}>Reject</button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </section>
        {/* Add Family Form */}
        <section className="bg-white rounded shadow p-6 sticky top-4">
          <h2 className="text-2xl font-semibold mb-6">Add Family</h2>
          <input className="input mb-2" placeholder="Family ID" value={newFamily.familyId} onChange={e => setNewFamily({ ...newFamily, familyId: e.target.value })} />
          <input className="input mb-2" placeholder="Leader Name" value={newFamily.leaderName} onChange={e => setNewFamily({ ...newFamily, leaderName: e.target.value })} />
          <input className="input mb-2" placeholder="Email" value={newFamily.email} onChange={e => setNewFamily({ ...newFamily, email: e.target.value })} />
          <input className="input mb-2" placeholder="Password" type="password" value={newFamily.password} onChange={e => setNewFamily({ ...newFamily, password: e.target.value })} />
          <input className="input mb-2" placeholder="Members (comma separated)" value={newFamily.members} onChange={e => setNewFamily({ ...newFamily, members: e.target.value })} />
          <input className="input mb-2" placeholder="Phone" value={newFamily.phone} onChange={e => setNewFamily({ ...newFamily, phone: e.target.value })} />
          <input className="input mb-2" placeholder="Address" value={newFamily.address} onChange={e => setNewFamily({ ...newFamily, address: e.target.value })} />
          <button className="btn-blue w-full" onClick={createFamily} disabled={loading}>Create</button>
        </section>
      </section>

      {/* Tax section */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <section className="bg-white rounded shadow p-6">
          <h2 className="text-lg font-semibold mb-4">Add Tax</h2>
          <label className="block mb-2">
            <input type="checkbox" checked={selectAll} onChange={() => {
              if(selectAll) setSelectedFamilies([]); else setSelectedFamilies(families.map(f => f._id));
              setSelectAll(!selectAll);
            }} /> Select All
          </label>
          <input type="month" className="input mb-2" value={taxMonth} onChange={e => setTaxMonth(e.target.value)} />
          <input type="number" className="input mb-2" placeholder="Amount" value={taxAmount} onChange={e => setTaxAmount(e.target.value)} />
          <label className="block mb-3"><input type="checkbox" checked={taxPaid} onChange={e => setTaxPaid(e.target.checked)} /> Mark Paid</label>
          <button className="btn-green w-full" onClick={addTax} disabled={loading}>Add Tax</button>
          <p className="text-center text-lg font-bold mt-4">Total Paid: ₹{totalPaid}</p>
        </section>
        <section className="bg-white rounded shadow p-6 max-h-96 overflow-auto">
          <h2 className="text-lg font-semibold mb-4">Paid Families</h2>
          {!paidFamilies.length && <p>No paid families.</p>}
          {paidFamilies.map(f => (
            <div key={f._id} className="bg-green-100 rounded p-2 mb-2 flex justify-between">
              <span>{f.familyId} - {f.leaderName}</span>
              <strong>₹{f.taxHistory.find(t => t.month === taxMonth && t.paid)?.amount || 0}</strong>
            </div>
          ))}
        </section>
        <section className="bg-white rounded shadow p-6 max-h-96 overflow-auto">
          <h2 className="text-lg font-semibold mb-4">Pending Families</h2>
          {!unpaidFamilies.length && <p>No pending families.</p>}
          {unpaidFamilies.map(f => {
            const amt = f.taxHistory.find(t => t.month === taxMonth)?.amount || taxAmount || "-";
            return (
              <div key={f._id} className="bg-red-100 rounded p-2 mb-2 flex justify-between">
                <span>{f.familyId} - {f.leaderName} • ₹{amt}</span>
                <button className="btn-blue" onClick={() => markTaxPaid(f._id, taxMonth)} disabled={loading}>Mark Paid</button>
              </div>
            );
          })}
        </section>
      </section>

      {/* Events & Workers */}
      <section className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-12">
        <section className="bg-white rounded shadow p-6">
          <h2 className="text-lg font-semibold mb-4">Events</h2>
          <ul className="max-h-64 overflow-auto divide-y divide-gray-200">
            {!events.length && <p>No events found.</p>}
            {events.map(ev => (
              <li key={ev._id} className="flex justify-between py-2">
                <span>{ev.title} ({new Date(ev.date).toLocaleDateString()})</span>
                <button className="btn-red btn-xs" onClick={() => deleteEvent(ev._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-2 mt-4">
            <input className="input" placeholder="Title" value={newEvent.title} onChange={e => setNewEvent({ ...newEvent, title: e.target.value })} />
            <input className="input" type="date" value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} />
            <button className="btn-green" onClick={addEvent}>Add Event</button>
          </div>
        </section>

        <section className="bg-white rounded shadow p-6">
          <h2 className="text-lg font-semibold mb-4">Workers</h2>
          <ul className="max-h-64 overflow-auto divide-y divide-gray-200">
            {!workers.length && <p>No workers found.</p>}
            {workers.map(w => (
              <li key={w._id} className="flex justify-between py-2">
                <span>{w.type} — {w.description}</span>
                <button className="btn-red btn-xs" onClick={() => deleteWorker(w._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="grid gap-2 mt-4">
            <input className="input" placeholder="Type" value={newWorker.type} onChange={e => setNewWorker({ ...newWorker, type: e.target.value })} />
            <input className="input" placeholder="Description" value={newWorker.description} onChange={e => setNewWorker({ ...newWorker, description: e.target.value })} />
            <button className="btn-green" onClick={addWorker}>Add Worker</button>
          </div>
        </section>
      </section>

      {/* History & Gallery */}
      <section className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <section className="bg-white rounded shadow p-6">
          <h2 className="text-lg font-semibold mb-4">History</h2>
          <ul className="max-h-64 overflow-auto divide-y divide-gray-200">
            {!historyItems.length && <p>No history records.</p>}
            {historyItems.map(h => (
              <li key={h._id} className="flex justify-between py-2">
                <p>{h.content?.en || h.content?.ta}</p>
                <button className="btn-red btn-xs" onClick={() => deleteHistory(h._id)}>Delete</button>
              </li>
            ))}
          </ul>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-2 mt-4">
            <input className="input" placeholder="Content English" value={newHistory.contentEN} onChange={e => setNewHistory({ ...newHistory, contentEN: e.target.value })} />
            <input className="input" placeholder="Content Other" value={newHistory.contentTA} onChange={e => setNewHistory({ ...newHistory, contentTA: e.target.value })} />
            <button className="btn-green" onClick={addHistory}>Add History</button>
          </div>
        </section>

        <section className="bg-white rounded shadow p-6">
          <h2 className="text-lg font-semibold mb-4">Gallery</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-4">
            {!gallery.length && <p>No gallery items.</p>}
            {gallery.map(g => (
              <div key={g._id} className="border rounded p-4 flex flex-col">
                <a href={`${API_BASE}${g.url}`} target="_blank" rel="noreferrer" className="mb-2 text-blue-600 truncate hover:underline">{g.title || "Untitled"}</a>
                <p className="text-sm text-gray-500 mb-4 truncate">{g.description}</p>
                <button className="btn-red btn-xs mt-auto" onClick={() => deleteGallery(g._id)}>Delete</button>
              </div>
            ))}
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-2">
            <input type="file" onChange={e => setGalleryFile(e.target.files[0])} />
            <input className="input" placeholder="Title" value={galleryMeta.title} onChange={e => setGalleryMeta({ ...galleryMeta, title: e.target.value })} />
            <input className="input" placeholder="Description" value={galleryMeta.description} onChange={e => setGalleryMeta({ ...galleryMeta, description: e.target.value })} />
            <button className="btn-green" onClick={uploadGallery} disabled={!galleryFile}>Upload</button>
          </div>
        </section>
      </section>
    </div>
  );
};

export default AdminDashboard;
