// Placeholder for client/src/pages/History.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useTranslation } from "react-i18next";

const History = () => {
  const { t, i18n } = useTranslation();
  const [history, setHistory] = useState("");

  useEffect(() => {
    // Fetch history content from backend API
    axios.get("/api/village/history")
      .then(res => {
        // Choose content based on selected language
        const lang = i18n.language;
        setHistory(res.data.content[lang]);
      })
      .catch(err => console.log(err));
  }, [i18n.language]);

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">{t("history")}</h2>
      <p className="text-lg">{history}</p>
    </div>
  );
};

export default History;
