import React, { useEffect, useState } from "react";
import axios from "axios";

const FamilyDashboard = () => {
  const [family, setFamily] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("familyToken"); // store family token on login

  useEffect(() => {
    const fetchFamily = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/family/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFamily(res.data);
      } catch (err) {
        console.error(err.response?.data || err.message);
        setError("Failed to load family data.");
      } finally {
        setLoading(false);
      }
    };

    if (token) fetchFamily();
  }, [token]);

  if (loading) return <p className="p-6 text-gray-600">Loading your family info...</p>;
  if (error) return <p className="p-6 text-red-500">{error}</p>;

  const handleUpload = () => {
    // implement document upload functionality here
    alert("Upload document functionality goes here.");
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">üë®‚Äçüë©‚Äçüëß‚Äçüë¶ My Family</h2>

      <div className="border p-4 rounded shadow mb-4">
        <p><strong>Family ID:</strong> {family.familyId}</p>
        <p><strong>Leader Name:</strong> {family.leaderName}</p>
        <p><strong>Members:</strong> {family.members.join(", ")}</p>
        <p><strong>Address:</strong> {family.address}</p>
        <p><strong>Phone:</strong> {family.phone}</p>
        <p><strong>Email:</strong> {family.email}</p>
      </div>

      <button
        onClick={handleUpload}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Upload Documents
      </button>
    </div>
  );
};

export default FamilyDashboard;
