import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const token = localStorage.getItem("adminToken");
  const headers = { Authorization: `Bearer ${token}` };

  const [families, setFamilies] = useState([]);
  const [events, setEvents] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [history, setHistory] = useState([]);
  const [gallery, setGallery] = useState([]);
  const [message, setMessage] = useState("");

  // Forms
  const [newFamily, setNewFamily] = useState({});
  const [newEvent, setNewEvent] = useState({});
  const [newWorker, setNewWorker] = useState({});
  const [newHistory, setNewHistory] = useState({});
  const [galleryFile, setGalleryFile] = useState(null);

  // Load all data
  const loadAll = async () => {
    try {
      const [fRes, eRes, wRes, hRes, gRes] = await Promise.all([
        axios.get("http://localhost:5000/api/admin/families", { headers }),
        axios.get("http://localhost:5000/api/admin/events", { headers }),
        axios.get("http://localhost:5000/api/admin/workers", { headers }),
        axios.get("http://localhost:5000/api/admin/history", { headers }),
        axios.get("http://localhost:5000/api/admin/gallery", { headers }),
      ]);
      setFamilies(fRes.data);
      setEvents(eRes.data);
      setWorkers(wRes.data);
      setHistory(hRes.data);
      setGallery(gRes.data);
    } catch (err) {
      console.error("Error loading data:", err);
    }
  };

  useEffect(() => {
    loadAll();
  }, []);

  // Family actions
  const approveFamily = async (id, approved) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/approve",
        { familyId: id, approved },
        { headers }
      );
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  const createFamily = async () => {
    try {
      await axios.post("http://localhost:5000/api/admin/families", newFamily, { headers });
      setNewFamily({});
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  const updateTax = async (familyId, month, amount) => {
    try {
      await axios.post(
        "http://localhost:5000/api/admin/families/tax",
        { familyId, month, amount },
        { headers }
      );
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  const sendNotifications = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/families/notify-tax",
        {},
        { headers }
      );
      setMessage(res.data.message);
    } catch (err) {
      console.error(err);
    }
  };

  // Events CRUD
  const addEvent = async () => {
    try {
      await axios.post("http://localhost:5000/api/admin/events", newEvent, { headers });
      setNewEvent({});
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };
  const delEvent = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/events/${id}`, { headers });
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  // Workers CRUD
  const addWorker = async () => {
    try {
      await axios.post("http://localhost:5000/api/admin/workers", newWorker, { headers });
      setNewWorker({});
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };
  const delWorker = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/workers/${id}`, { headers });
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  // History CRUD
  const addHistory = async () => {
    try {
      await axios.post("http://localhost:5000/api/admin/history", newHistory, { headers });
      setNewHistory({});
      loadAll();
    } catch (err) {
      console.error(err);
    }
  };

  // Gallery upload
  const uploadGallery = async () => {
    if (!galleryFile) return alert("Select a file first!");
    const formData = new FormData();
    formData.append("image", galleryFile);
    try {
      await axios.post("http://localhost:5000/api/admin/gallery", formData, {
        headers: { ...headers, "Content-Type": "multipart/form-data" },
      });
      setGalleryFile(null);
      loadAll();
    } catch (err) {
      console.error("Gallery upload error:", err);
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      <h1 className="text-2xl font-bold">Admin Dashboard</h1>

      {/* Families */}
      <div className="p-4 border rounded">
        <h2 className="font-semibold">Families</h2>
        <button
          className="bg-blue-600 text-white px-3 py-1 rounded mb-2"
          onClick={sendNotifications}
        >
          Send Tax Notifications
        </button>
        <table className="w-full mt-3 border">
          <thead>
            <tr className="bg-gray-100">
              <th>FamilyId</th>
              <th>Leader</th>
              <th>Approved</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {families.map((f) => (
              <tr key={f._id}>
                <td className="p-2">{f.familyId}</td>
                <td className="p-2">{f.leaderName}</td>
                <td className="p-2">{f.approved ? "Yes" : "No"}</td>
                <td className="p-2 space-x-2">
                  <button
                    onClick={() => approveFamily(f._id, true)}
                    className="bg-green-600 text-white px-2 py-1 rounded"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => approveFamily(f._id, false)}
                    className="bg-red-600 text-white px-2 py-1 rounded"
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="mt-4">
          <h3 className="font-semibold">Create Family</h3>
          <input
            placeholder="familyId"
            value={newFamily.familyId || ""}
            onChange={(e) =>
              setNewFamily({ ...newFamily, familyId: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <input
            placeholder="password"
            value={newFamily.password || ""}
            onChange={(e) =>
              setNewFamily({ ...newFamily, password: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <input
            placeholder="leader"
            value={newFamily.leaderName || ""}
            onChange={(e) =>
              setNewFamily({ ...newFamily, leaderName: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <button
            onClick={createFamily}
            className="bg-indigo-600 text-white px-3 py-1 rounded"
          >
            Create
          </button>
        </div>
      </div>

      {/* Events & Workers */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Events */}
        <div className="p-4 border rounded">
          <h3 className="font-semibold">Events</h3>
          <input
            placeholder="title"
            value={newEvent.title || ""}
            onChange={(e) =>
              setNewEvent({ ...newEvent, title: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <input
            type="date"
            value={newEvent.date || ""}
            onChange={(e) =>
              setNewEvent({ ...newEvent, date: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <button
            onClick={addEvent}
            className="bg-blue-600 text-white px-3 py-1 rounded"
          >
            Add
          </button>
          <ul className="mt-3">
            {events.map((ev) => (
              <li key={ev._id} className="flex justify-between">
                <span>
                  {ev.title} ({new Date(ev.date).toLocaleDateString()})
                </span>
                <button
                  onClick={() => delEvent(ev._id)}
                  className="text-red-600"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Workers */}
        <div className="p-4 border rounded">
          <h3 className="font-semibold">Workers</h3>
          <input
            placeholder="type"
            value={newWorker.type || ""}
            onChange={(e) =>
              setNewWorker({ ...newWorker, type: e.target.value })
            }
            className="border p-2 mr-2"
          />
          <button
            onClick={addWorker}
            className="bg-blue-600 text-white px-3 py-1 rounded"
          >
            Add
          </button>
          <ul className="mt-3">
            {workers.map((w) => (
              <li key={w._id} className="flex justify-between">
                <span>{w.type}</span>
                <button
                  onClick={() => delWorker(w._id)}
                  className="text-red-600"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* History & Gallery */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="p-4 border rounded">
          <h3 className="font-semibold">History</h3>
          <input
            placeholder="EN content"
            onChange={(e) =>
              setNewHistory({
                ...newHistory,
                content: { ...(newHistory.content || {}), en: e.target.value },
              })
            }
            className="border p-2 w-full mb-2"
          />
          <input
            placeholder="TA content"
            onChange={(e) =>
              setNewHistory({
                ...newHistory,
                content: { ...(newHistory.content || {}), ta: e.target.value },
              })
            }
            className="border p-2 w-full mb-2"
          />
          <button
            onClick={addHistory}
            className="bg-blue-600 text-white px-3 py-1 rounded"
          >
            Add
          </button>
          <ul className="mt-3">
            {history.map((h) => (
              <li key={h._id}>
                {h.content?.en || "no en"} / {h.content?.ta || "no ta"}
              </li>
            ))}
          </ul>
        </div>

        <div className="p-4 border rounded">
          <h3 className="font-semibold">Gallery</h3>
          <input
            type="file"
            onChange={(e) => setGalleryFile(e.target.files[0])}
          />
          <button
            onClick={uploadGallery}
            className="bg-blue-600 text-white px-3 py-1 rounded mt-2"
          >
            Upload
          </button>
          <div className="mt-3 grid grid-cols-3 gap-2">
            {gallery.map((g) => (
              <img
                key={g._id}
                src={g.url}
                alt={g.title}
                className="w-full h-24 object-cover"
              />
            ))}
          </div>
        </div>
      </div>

      {message && <p className="text-green-700">{message}</p>}
    </div>
  );
};

export default AdminDashboard;
